class MathModelBacktester:
    def __init__(self, config: BacktestConfig):
        self.config = config
        self.price_generator = NormalDistributionPriceGenerator()
        self.monte_carlo_engine = MonteCarloEngine()

    def run_backtest(self, strategy: Strategy, params: dict) -> BacktestResult:
        # ç”ŸæˆåŸºæ–¼æ­£æ…‹åˆ†ä½ˆçš„åƒ¹æ ¼åºåˆ—
        prices = self.price_generator.generate_prices(
            initial_price=params['initial_price'],
            volatility=params['volatility'],
            drift=params['drift'],
            periods=params['periods']
        )

        # åŸ·è¡Œç­–ç•¥å›æ¸¬
        return self._execute_strategy(strategy, prices)

class NormalDistributionPriceGenerator:
    def generate_prices(self, initial_price: float, volatility: float, 
                       drift: float, periods: int) -> np.ndarray:
        # ä½¿ç”¨å¹¾ä½•å¸ƒæœ—é‹å‹•ç”Ÿæˆåƒ¹æ ¼
        dt = 1/252  # æ—¥é »ç‡
        random_shocks = np.random.normal(0, 1, periods)
        returns = drift * dt + volatility * np.sqrt(dt) * random_shocks
        prices = initial_price * np.exp(np.cumsum(returns))
        return prices
