use rust_decimal::Decimal;
use chrono::{DateTime, Utc};
use serde::{Deserialize, Serialize};
use crate::types::{OrderUpdate, Position, OrderSide};
use std::collections::HashMap;

/// ç›ˆè™§è¨ˆç®—å™¨
#[derive(Debug, Clone)]
pub struct PnLCalculator {
    /// æŒå€‰è¨˜éŒ„
    positions: HashMap<String, Position>,
    /// å·²å¯¦ç¾ç›ˆè™§è¨˜éŒ„
    realized_pnl_history: Vec<RealizedPnL>,
    /// ç•¶æ—¥çµ±è¨ˆ
    daily_stats: DailyPnLStats,
}

/// å·²å¯¦ç¾ç›ˆè™§è¨˜éŒ„
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct RealizedPnL {
    pub symbol: String,
    pub side: OrderSide,
    pub quantity: Decimal,
    pub entry_price: Decimal,
    pub exit_price: Decimal,
    pub realized_pnl: Decimal,
    pub commission: Decimal,
    pub roi: f64, // æŠ•è³‡å ±é…¬ç‡
    pub holding_time: chrono::Duration,
    pub timestamp: DateTime<Utc>,
}

/// æ¯æ—¥ç›ˆè™§çµ±è¨ˆ
#[derive(Debug, Clone, Default, Serialize, Deserialize)]
pub struct DailyPnLStats {
    pub date: String,
    pub total_trades: u32,
    pub winning_trades: u32,
    pub losing_trades: u32,
    pub total_realized_pnl: Decimal,
    pub total_unrealized_pnl: Decimal,
    pub total_commission: Decimal,
    pub total_volume: Decimal,
    pub win_rate: f64,
    pub largest_win: Decimal,
    pub largest_loss: Decimal,
    pub average_win: Decimal,
    pub average_loss: Decimal,
    pub profit_factor: Option<f64>, // ç›ˆè™§æ¯”ç‡
}

/// äº¤æ˜“åŸ·è¡Œçµæœ
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct TradeExecutionResult {
    pub symbol: String,
    pub side: OrderSide,
    pub quantity: Decimal,
    pub price: Decimal,
    pub commission: Decimal,
    pub realized_pnl: Option<Decimal>,
    pub unrealized_pnl: Option<Decimal>,
    pub total_value: Decimal,
    pub roi: Option<f64>,
    pub is_position_closed: bool,
}

impl PnLCalculator {
    /// å‰µå»ºæ–°çš„ç›ˆè™§è¨ˆç®—å™¨
    pub fn new() -> Self {
        Self {
            positions: HashMap::new(),
            realized_pnl_history: Vec::new(),
            daily_stats: DailyPnLStats::default(),
        }
    }

    /// è™•ç†è¨‚å–®æ›´æ–°ï¼Œè¨ˆç®—ç›ˆè™§
    pub fn process_order_update(&mut self, order_update: &OrderUpdate, 
                               symbol: &str, side: &OrderSide, 
                               quantity: Decimal, price: Decimal, 
                               commission: Decimal) -> TradeExecutionResult {
        let total_value = quantity * price;

        // æª¢æŸ¥æ˜¯å¦æœ‰ç¾æœ‰æŒå€‰
        let existing_position = self.positions.get_mut(symbol);

        match existing_position {
            Some(position) => {
                // è¨ˆç®—ç›ˆè™§ä¸¦æ›´æ–°æŒå€‰
                self.update_position_with_trade(position, side, quantity, price, commission)
            }
            None => {
                // æ–°é–‹å€‰ä½
                self.open_new_position(symbol, side, quantity, price, commission)
            }
        }
    }

    /// é–‹æ–°å€‰ä½
    fn open_new_position(&mut self, symbol: &str, side: &OrderSide, 
                        quantity: Decimal, price: Decimal, 
                        commission: Decimal) -> TradeExecutionResult {
        let position = Position {
            symbol: symbol.to_string(),
            side: side.clone(),
            quantity,
            average_price: price,
            unrealized_pnl: Decimal::ZERO,
            timestamp: Utc::now(),
        };

        self.positions.insert(symbol.to_string(), position);

        TradeExecutionResult {
            symbol: symbol.to_string(),
            side: side.clone(),
            quantity,
            price,
            commission,
            realized_pnl: None,
            unrealized_pnl: Some(Decimal::ZERO),
            total_value: quantity * price,
            roi: None,
            is_position_closed: false,
        }
    }

    /// æ›´æ–°ç¾æœ‰æŒå€‰
    fn update_position_with_trade(&mut self, position: &mut Position, 
                                 side: &OrderSide, quantity: Decimal, 
                                 price: Decimal, commission: Decimal) -> TradeExecutionResult {
        let is_closing_trade = position.side != *side;

        if is_closing_trade {
            // å¹³å€‰äº¤æ˜“ - è¨ˆç®—å·²å¯¦ç¾ç›ˆè™§
            self.close_position(position, quantity, price, commission)
        } else {
            // åŠ å€‰äº¤æ˜“ - æ›´æ–°å¹³å‡æˆæœ¬
            self.add_to_position(position, quantity, price, commission)
        }
    }

    /// å¹³å€‰è¨ˆç®—
    fn close_position(&mut self, position: &mut Position, 
                     close_quantity: Decimal, close_price: Decimal, 
                     commission: Decimal) -> TradeExecutionResult {
        let quantity_to_close = close_quantity.min(position.quantity);
        let entry_price = position.average_price;

        // è¨ˆç®—å·²å¯¦ç¾ç›ˆè™§
        let price_diff = match position.side {
            OrderSide::Buy => close_price - entry_price,
            OrderSide::Sell => entry_price - close_price,
        };

        let gross_pnl = price_diff * quantity_to_close;
        let realized_pnl = gross_pnl - commission;

        // è¨ˆç®—ROI
        let investment = entry_price * quantity_to_close;
        let roi = if investment > Decimal::ZERO {
            (realized_pnl / investment * Decimal::from(100)).to_f64().unwrap_or(0.0)
        } else {
            0.0
        };

        // è¨˜éŒ„å·²å¯¦ç¾ç›ˆè™§
        let holding_time = Utc::now() - position.timestamp;
        let pnl_record = RealizedPnL {
            symbol: position.symbol.clone(),
            side: position.side.clone(),
            quantity: quantity_to_close,
            entry_price,
            exit_price: close_price,
            realized_pnl,
            commission,
            roi,
            holding_time,
            timestamp: Utc::now(),
        };

        self.realized_pnl_history.push(pnl_record);

        // æ›´æ–°æŒå€‰æ•¸é‡
        position.quantity -= quantity_to_close;
        let is_position_closed = position.quantity == Decimal::ZERO;

        // å¦‚æœå®Œå…¨å¹³å€‰ï¼Œç§»é™¤æŒå€‰è¨˜éŒ„
        if is_position_closed {
            self.positions.remove(&position.symbol);
        }

        // æ›´æ–°æ¯æ—¥çµ±è¨ˆ
        self.update_daily_stats(&realized_pnl);

        TradeExecutionResult {
            symbol: position.symbol.clone(),
            side: match position.side { OrderSide::Buy => OrderSide::Sell, OrderSide::Sell => OrderSide::Buy },
            quantity: quantity_to_close,
            price: close_price,
            commission,
            realized_pnl: Some(realized_pnl),
            unrealized_pnl: None,
            total_value: close_price * quantity_to_close,
            roi: Some(roi),
            is_position_closed,
        }
    }

    /// åŠ å€‰è¨ˆç®—
    fn add_to_position(&mut self, position: &mut Position, 
                      add_quantity: Decimal, add_price: Decimal, 
                      commission: Decimal) -> TradeExecutionResult {
        // è¨ˆç®—æ–°çš„å¹³å‡æˆæœ¬
        let total_cost = (position.average_price * position.quantity) + (add_price * add_quantity);
        let total_quantity = position.quantity + add_quantity;

        position.average_price = total_cost / total_quantity;
        position.quantity = total_quantity;

        TradeExecutionResult {
            symbol: position.symbol.clone(),
            side: position.side.clone(),
            quantity: add_quantity,
            price: add_price,
            commission,
            realized_pnl: None,
            unrealized_pnl: Some(Decimal::ZERO), // åŠ å€‰æ™‚æœªå¯¦ç¾ç›ˆè™§é‡æ–°è¨ˆç®—
            total_value: add_price * add_quantity,
            roi: None,
            is_position_closed: false,
        }
    }

    /// è¨ˆç®—æœªå¯¦ç¾ç›ˆè™§
    pub fn calculate_unrealized_pnl(&self, symbol: &str, current_price: Decimal) -> Option<Decimal> {
        if let Some(position) = self.positions.get(symbol) {
            let price_diff = match position.side {
                OrderSide::Buy => current_price - position.average_price,
                OrderSide::Sell => position.average_price - current_price,
            };
            Some(price_diff * position.quantity)
        } else {
            None
        }
    }

    /// æ›´æ–°æ¯æ—¥çµ±è¨ˆ
    fn update_daily_stats(&mut self, realized_pnl: &Decimal) {
        self.daily_stats.total_trades += 1;
        self.daily_stats.total_realized_pnl += *realized_pnl;

        if *realized_pnl > Decimal::ZERO {
            self.daily_stats.winning_trades += 1;
            if *realized_pnl > self.daily_stats.largest_win {
                self.daily_stats.largest_win = *realized_pnl;
            }
        } else if *realized_pnl < Decimal::ZERO {
            self.daily_stats.losing_trades += 1;
            if *realized_pnl < self.daily_stats.largest_loss {
                self.daily_stats.largest_loss = *realized_pnl;
            }
        }

        // æ›´æ–°å‹ç‡
        if self.daily_stats.total_trades > 0 {
            self.daily_stats.win_rate = 
                (self.daily_stats.winning_trades as f64) / (self.daily_stats.total_trades as f64) * 100.0;
        }

        // æ›´æ–°å¹³å‡ç›ˆè™§
        if self.daily_stats.winning_trades > 0 {
            let total_wins: Decimal = self.realized_pnl_history
                .iter()
                .filter(|pnl| pnl.realized_pnl > Decimal::ZERO)
                .map(|pnl| pnl.realized_pnl)
                .sum();
            self.daily_stats.average_win = total_wins / Decimal::from(self.daily_stats.winning_trades);
        }

        if self.daily_stats.losing_trades > 0 {
            let total_losses: Decimal = self.realized_pnl_history
                .iter()
                .filter(|pnl| pnl.realized_pnl < Decimal::ZERO)
                .map(|pnl| pnl.realized_pnl)
                .sum();
            self.daily_stats.average_loss = total_losses / Decimal::from(self.daily_stats.losing_trades);
        }
    }

    /// ç²å–æ¯æ—¥çµ±è¨ˆ
    pub fn get_daily_stats(&self) -> &DailyPnLStats {
        &self.daily_stats
    }

    /// é‡ç½®æ¯æ—¥çµ±è¨ˆï¼ˆæ–°çš„ä¸€å¤©é–‹å§‹æ™‚èª¿ç”¨ï¼‰
    pub fn reset_daily_stats(&mut self) {
        let today = Utc::now().format("%Y-%m-%d").to_string();
        self.daily_stats = DailyPnLStats {
            date: today,
            ..Default::default()
        };
    }

    /// ç²å–ç•¶å‰æ´»èºæŒå€‰æ•¸é‡
    pub fn get_active_positions_count(&self) -> u32 {
        self.positions.len() as u32
    }

    /// è¨ˆç®—ç¸½æŠ•è³‡çµ„åˆåƒ¹å€¼ï¼ˆéœ€è¦ç•¶å‰å¸‚åƒ¹ï¼‰
    pub fn calculate_portfolio_value(&self, market_prices: &HashMap<String, Decimal>) -> Decimal {
        let mut total_value = Decimal::ZERO;

        for (symbol, position) in &self.positions {
            if let Some(current_price) = market_prices.get(symbol) {
                total_value += *current_price * position.quantity;
            } else {
                // å¦‚æœæ²’æœ‰ç•¶å‰å¸‚åƒ¹ï¼Œä½¿ç”¨å¹³å‡æˆæœ¬
                total_value += position.average_price * position.quantity;
            }
        }

        total_value
    }
}

impl Default for PnLCalculator {
    fn default() -> Self {
        Self::new()
    }
}

#[cfg(test)]
mod tests {
    use super::*;

    #[test]
    fn test_new_position_creation() {
        let mut calculator = PnLCalculator::new();
        let result = calculator.open_new_position(
            "BTCUSDT", 
            &OrderSide::Buy, 
            Decimal::new(1, 1), // 0.1
            Decimal::from(50000), 
            Decimal::new(5, 0) // 5.0 commission
        );

        assert_eq!(result.symbol, "BTCUSDT");
        assert_eq!(result.quantity, Decimal::new(1, 1));
        assert_eq!(result.price, Decimal::from(50000));
        assert!(!result.is_position_closed);
        assert!(result.realized_pnl.is_none());
    }

    #[test]
    fn test_position_closing_with_profit() {
        let mut calculator = PnLCalculator::new();

        // é–‹å€‰
        let mut position = Position {
            symbol: "BTCUSDT".to_string(),
            side: OrderSide::Buy,
            quantity: Decimal::new(1, 1), // 0.1
            average_price: Decimal::from(50000),
            unrealized_pnl: Decimal::ZERO,
            timestamp: Utc::now(),
        };

        // å¹³å€‰ç²åˆ©
        let result = calculator.close_position(
            &mut position,
            Decimal::new(1, 1), // 0.1
            Decimal::from(52000), // è³£å‡ºåƒ¹æ ¼æ›´é«˜
            Decimal::from(5) // commission
        );

        assert!(result.realized_pnl.unwrap() > Decimal::ZERO);
        assert!(result.roi.unwrap() > 0.0);
        assert!(result.is_position_closed);
    }

    #[test]
    fn test_daily_stats_update() {
        let mut calculator = PnLCalculator::new();

        // æ¨¡æ“¬ä¸€äº›äº¤æ˜“
        calculator.update_daily_stats(&Decimal::from(100));
        calculator.update_daily_stats(&Decimal::from(-50));
        calculator.update_daily_stats(&Decimal::from(200));

        let stats = calculator.get_daily_stats();
        assert_eq!(stats.total_trades, 3);
        assert_eq!(stats.winning_trades, 2);
        assert_eq!(stats.losing_trades, 1);
        assert_eq!(stats.win_rate, 66.66666666666667);
    }
}

