} => {
    let title = format!("{} Order Filled - {}", self.get_side_emoji(&side), symbol);
    let mut content = format!("Order filled for {} {} at {}", 
        symbol, side.to_uppercase(), self.format_currency(&price));

    // æ·»åŠ ç›ˆè™§ä¿¡æ¯åˆ°å…§å®¹
    if let Some(ref pnl) = realized_pnl {
        let pnl_emoji = if pnl.starts_with('-') { "ğŸ“‰" } else { "ğŸ“ˆ" };
        content.push_str(&format!(" | {} Realized P&L: {}", pnl_emoji, self.format_currency(pnl)));
    }

    let mut message = NotificationMessage::new(NotificationType::Trade, title, content)
        .with_color(self.get_color(&NotificationType::Trade, &Priority::High))
        .with_priority(Priority::High)
        .add_field("Symbol".to_string(), symbol, true)
        .add_field("Side".to_string(), side.to_uppercase(), true)
        .add_field("Quantity".to_string(), quantity, true)
        .add_field("Fill Price".to_string(), self.format_currency(&price), true)
        .add_field("Total Value".to_string(), self.format_currency(&total_value), true)
        .add_field("Commission".to_string(), self.format_currency(&commission), true);

    // æ·»åŠ ç›ˆè™§ç›¸é—œæ¬„ä½
    if let Some(ref pnl) = realized_pnl {
        message = message.add_field("Realized P&L".to_string(), self.format_currency(pnl), true);
    }

    if let Some(ref unrealized) = unrealized_pnl {
        message = message.add_field("Unrealized P&L".to_string(), self.format_currency(unrealized), true);
