use super::{
    DiscordBot, MessageFormatter, TradingMessageFormatter, 
    PnLCalculator, TradeExecutionResult, TradingEvent,
    NotificationMessage, NotificationType, ErrorSeverity, Priority
};
use crate::types::{OrderUpdate, OrderSide, OrderStatus};
use rust_decimal::Decimal;
use chrono::Utc;
use std::collections::HashMap;
use tokio::sync::RwLock;
use std::sync::Arc;

/// æ•´åˆçš„äº¤æ˜“é€šçŸ¥æœå‹™
/// è² è²¬è™•ç†äº¤æ˜“äº‹ä»¶ã€è¨ˆç®—ç›ˆè™§ä¸¦ç™¼é€ Discord é€šçŸ¥
#[derive(Debug)]
pub struct TradingNotificationService {
    discord_bot: Arc<DiscordBot>,
    pnl_calculator: Arc<RwLock<PnLCalculator>>,
    formatter: TradingMessageFormatter,
    current_market_prices: Arc<RwLock<HashMap<String, Decimal>>>,
}

impl TradingNotificationService {
    /// å‰µå»ºæ–°çš„äº¤æ˜“é€šçŸ¥æœå‹™
    pub fn new(discord_bot: Arc<DiscordBot>) -> Self {
        Self {
            discord_bot,
            pnl_calculator: Arc::new(RwLock::new(PnLCalculator::new())),
            formatter: TradingMessageFormatter::new(None),
            current_market_prices: Arc::new(RwLock::new(HashMap::new())),
        }
    }

    /// è™•ç†è¨‚å–®æ›´æ–°äº‹ä»¶ä¸¦ç™¼é€é€šçŸ¥
    pub async fn handle_order_update(
        &self,
        order_update: &OrderUpdate,
        symbol: &str,
        side: &OrderSide,
        quantity: Decimal,
        price: Decimal,
        commission: Decimal,
    ) -> Result<(), Box<dyn std::error::Error + Send + Sync>> {
        match order_update.status {
            OrderStatus::Filled | OrderStatus::PartiallyFilled => {
                // è¨ˆç®—ç›ˆè™§
                let trade_result = {
                    let mut calculator = self.pnl_calculator.write().await;
                    calculator.process_order_update(
                        order_update, 
                        symbol, 
                        side, 
                        quantity, 
                        price, 
                        commission
                    )
                };

                // å‰µå»ºè©³ç´°çš„äº¤æ˜“äº‹ä»¶
                let trading_event = TradingEvent::OrderFilled {
                    symbol: symbol.to_string(),
                    side: self.format_order_side(side),
                    quantity: quantity.to_string(),
                    price: price.to_string(),
                    order_id: order_update.order_id.clone(),
                    commission: commission.to_string(),
                    realized_pnl: trade_result.realized_pnl.map(|pnl| pnl.to_string()),
                    unrealized_pnl: trade_result.unrealized_pnl.map(|pnl| pnl.to_string()),
                    total_value: trade_result.total_value.to_string(),
                    roi: trade_result.roi,
                };

                // æ ¼å¼åŒ–ä¸¦ç™¼é€é€šçŸ¥
                let message = self.formatter.format_trading_event(trading_event);
                self.discord_bot.send_notification(message).await?;

                // å¦‚æœæ˜¯å®Œå…¨å¹³å€‰ï¼Œç™¼é€æŒå€‰é—œé–‰é€šçŸ¥
                if trade_result.is_position_closed && trade_result.realized_pnl.is_some() {
                    self.send_position_closed_notification(&trade_result).await?;
                }
            }
            OrderStatus::Cancelled => {
                // ç™¼é€å–æ¶ˆé€šçŸ¥
                let trading_event = TradingEvent::OrderCancelled {
                    symbol: symbol.to_string(),
                    side: self.format_order_side(side),
                    quantity: quantity.to_string(),
                    price: price.to_string(),
                    order_id: order_update.order_id.clone(),
                    reason: "Order cancelled by system or user".to_string(),
                };

                let message = self.formatter.format_trading_event(trading_event);
                self.discord_bot.send_notification(message).await?;
            }
            OrderStatus::Rejected => {
                // ç™¼é€äº¤æ˜“éŒ¯èª¤é€šçŸ¥
                self.send_trading_error_notification(
                    symbol,
                    "Order Rejected",
                    "Order was rejected by exchange",
                    Some(order_update.order_id.clone()),
                ).await?;
            }
            _ => {
                // å…¶ä»–ç‹€æ…‹æš«ä¸è™•ç†
            }
        }

        Ok(())
    }

    /// ç™¼é€æŒå€‰é—œé–‰é€šçŸ¥
    async fn send_position_closed_notification(
        &self,
        trade_result: &TradeExecutionResult,
    ) -> Result<(), Box<dyn std::error::Error + Send + Sync>> {
        let holding_time = "N/A".to_string(); // å¯¦éš›å¯¦ç¾ä¸­æ‡‰è¨ˆç®—æŒå€‰æ™‚é–“

        let trading_event = TradingEvent::PositionClosed {
            symbol: trade_result.symbol.clone(),
            side: self.format_order_side(&trade_result.side),
            size: trade_result.quantity.to_string(),
            exit_price: trade_result.price.to_string(),
            realized_pnl: trade_result.realized_pnl.unwrap_or_default().to_string(),
            holding_time,
        };

        let message = self.formatter.format_trading_event(trading_event);
        self.discord_bot.send_notification(message).await?;

        Ok(())
    }

    /// ç™¼é€äº¤æ˜“éŒ¯èª¤é€šçŸ¥
    pub async fn send_trading_error_notification(
        &self,
        symbol: &str,
        error_type: &str,
        error_message: &str,
        order_id: Option<String>,
    ) -> Result<(), Box<dyn std::error::Error + Send + Sync>> {
        let trading_event = TradingEvent::TradingError {
            symbol: symbol.to_string(),
            error_type: error_type.to_string(),
            error_message: error_message.to_string(),
            order_id,
            timestamp: Utc::now().format("%Y-%m-%d %H:%M:%S UTC").to_string(),
        };

        let message = self.formatter.format_trading_event(trading_event);
        self.discord_bot.send_notification(message).await?;

        Ok(())
    }

    /// ç™¼é€æ—¥çµ‚å ±å‘Š
    pub async fn send_daily_report(&self) -> Result<(), Box<dyn std::error::Error + Send + Sync>> {
        let calculator = self.pnl_calculator.read().await;
        let stats = calculator.get_daily_stats();

        // è¨ˆç®—æŠ•è³‡çµ„åˆåƒ¹å€¼
        let market_prices = self.current_market_prices.read().await;
        let portfolio_value = calculator.calculate_portfolio_value(&market_prices);

        let trading_event = TradingEvent::DailyReport {
            date: stats.date.clone(),
            total_trades: stats.total_trades,
            winning_trades: stats.winning_trades,
            total_pnl: stats.total_realized_pnl.to_string(),
            total_volume: stats.total_volume.to_string(),
            win_rate: stats.win_rate,
            largest_win: stats.largest_win.to_string(),
            largest_loss: stats.largest_loss.to_string(),
            active_positions: calculator.get_active_positions_count(),
            portfolio_value: portfolio_value.to_string(),
        };

        let message = self.formatter.format_trading_event(trading_event);
        self.discord_bot.send_notification(message).await?;

        Ok(())
    }

    /// æ›´æ–°å¸‚å ´åƒ¹æ ¼ï¼ˆç”¨æ–¼è¨ˆç®—æœªå¯¦ç¾ç›ˆè™§ï¼‰
    pub async fn update_market_price(&self, symbol: String, price: Decimal) {
        let mut prices = self.current_market_prices.write().await;
        prices.insert(symbol, price);
    }

    /// é‡ç½®æ¯æ—¥çµ±è¨ˆï¼ˆæ¯å¤©é–‹å§‹æ™‚èª¿ç”¨ï¼‰
    pub async fn reset_daily_stats(&self) {
        let mut calculator = self.pnl_calculator.write().await;
        calculator.reset_daily_stats();
    }

    /// ç²å–ç•¶å‰ç›ˆè™§çµ±è¨ˆ
    pub async fn get_pnl_stats(&self) -> crate::discord::DailyPnLStats {
        let calculator = self.pnl_calculator.read().await;
        calculator.get_daily_stats().clone()
    }

    /// è¨ˆç®—æŒ‡å®šäº¤æ˜“å°çš„æœªå¯¦ç¾ç›ˆè™§
    pub async fn calculate_unrealized_pnl(&self, symbol: &str) -> Option<Decimal> {
        let calculator = self.pnl_calculator.read().await;
        let prices = self.current_market_prices.read().await;

        if let Some(current_price) = prices.get(symbol) {
            calculator.calculate_unrealized_pnl(symbol, *current_price)
        } else {
            None
        }
    }

    /// ç™¼é€ç³»çµ±éŒ¯èª¤é€šçŸ¥
    pub async fn send_system_error(
        &self,
        component: &str,
        error_message: &str,
        severity: ErrorSeverity,
    ) -> Result<(), Box<dyn std::error::Error + Send + Sync>> {
        let message = self.formatter.format_system_message(
            format!("System Error - {}", component),
            error_message.to_string(),
            severity,
        );

        self.discord_bot.send_notification(message).await?;
        Ok(())
    }

    /// è¼”åŠ©å‡½æ•¸ï¼šæ ¼å¼åŒ–è¨‚å–®æ–¹å‘
    fn format_order_side(&self, side: &OrderSide) -> String {
        match side {
            OrderSide::Buy => "BUY".to_string(),
            OrderSide::Sell => "SELL".to_string(),
        }
    }
}

/// å‰µå»ºäº¤æ˜“é€šçŸ¥æœå‹™çš„ä¾¿åˆ©å‡½æ•¸
pub async fn create_trading_notification_service(
    discord_token: String,
    default_channel_id: Option<String>,
) -> Result<TradingNotificationService, Box<dyn std::error::Error + Send + Sync>> {
    let discord_bot = Arc::new(DiscordBot::new(discord_token, default_channel_id).await?);
    Ok(TradingNotificationService::new(discord_bot))
}

