use serde_json::Value;
use std::collections::HashMap;
use anyhow::{Result, anyhow};

/// ç­–ç•¥è¼‰å…¥å™¨ï¼Œè² è²¬å¾JSONæª”æ¡ˆè¼‰å…¥å’Œè§£æç­–ç•¥é…ç½®
pub struct StrategyLoader {
    template_cache: HashMap<String, String>,
}

impl StrategyLoader {
    pub fn new() -> Self {
        Self {
            template_cache: HashMap::new(),
        }
    }

    /// å¾JSONæª”æ¡ˆè¼‰å…¥ç­–ç•¥é…ç½®
    pub async fn load_from_file(&self, file_path: &str) -> Result<StrategyConfig> {
        let content = tokio::fs::read_to_string(file_path).await?;
        self.parse_strategy_json(&content)
    }

    /// è§£æJSONç­–ç•¥é…ç½®
    pub fn parse_strategy_json(&self, json_content: &str) -> Result<StrategyConfig> {
        let value: Value = serde_json::from_str(json_content)?;

        // æå–ç­–ç•¥é…ç½®
        let strategy = value.get("strategy")
            .ok_or_else(|| anyhow!("Missing 'strategy' field in JSON"))?;

        let config = StrategyConfig {
            name: strategy.get("name")
                .and_then(|v| v.as_str())
                .unwrap_or("Unknown")
                .to_string(),
            version: strategy.get("version")
                .and_then(|v| v.as_str())
                .unwrap_or("1.0.0")
                .to_string(),
            description: strategy.get("description")
                .and_then(|v| v.as_str())
                .unwrap_or("")
                .to_string(),
            parameters: strategy.get("parameters")
                .cloned()
                .unwrap_or(Value::Object(serde_json::Map::new())),
            indicators: self.parse_indicators(strategy.get("indicators"))?,
            rules: self.parse_rules(strategy.get("rules"))?,
            risk_management: self.parse_risk_management(strategy.get("risk_management"))?,
        };

        Ok(config)
    }

/// è§£ææŒ‡æ¨™é…ç½®
fn parse_indicators(&self, indicators_value: Option<&Value>) -> Result<Vec<IndicatorConfig>> {
    let mut indicators = Vec::new();

    if let Some(Value::Array(indicator_array)) = indicators_value {
        for indicator_value in indicator_array {
            if let Value::Object(indicator_obj) = indicator_value {
                let config = IndicatorConfig {
                    name: indicator_obj.get("name")
                        .and_then(|v| v.as_str())
                        .unwrap_or("unknown")
                        .to_string(),
                    indicator_type: indicator_obj.get("type")
                        .and_then(|v| v.as_str())
                        .unwrap_or("SimpleMovingAverage")
                        .to_string(),
                    period: indicator_obj.get("period")
                        .and_then(|v| v.as_u64())
                        .unwrap_or(20) as u32,
                    parameters: indicator_obj.get("parameters")
                        .cloned()
                        .unwrap_or(Value::Object(serde_json::Map::new())),
                };
                indicators.push(config);
            }
        }
    }

    Ok(indicators)
}

/// è§£æäº¤æ˜“è¦å‰‡
fn parse_rules(&self, rules_value: Option<&Value>) -> Result<Vec<TradingRule>> {
    let mut rules = Vec::new();

    if let Some(Value::Array(rules_array)) = rules_value {
        for rule_value in rules_array {
            if let Value::Object(rule_obj) = rule_value {
                let rule = TradingRule {
                    name: rule_obj.get("name")
                        .and_then(|v| v.as_str())
                        .unwrap_or("unknown")
                        .to_string(),
                    condition: rule_obj.get("condition")
                        .and_then(|v| v.as_str())
                        .unwrap_or("")
                        .to_string(),
                    action: rule_obj.get("action")
                        .cloned()
                        .unwrap_or(Value::Object(serde_json::Map::new())),
                };
                rules.push(rule);
            }
        }
    }

    Ok(rules)
}

    /// è§£æé¢¨éšªç®¡ç†é…ç½®
    fn parse_risk_management(&self, risk_value: Option<&Value>) -> Result<RiskManagementConfig> {
        if let Some(Value::Object(risk_obj)) = risk_value {
            Ok(RiskManagementConfig {
                stop_loss: risk_obj.get("stop_loss")
                    .and_then(|v| v.as_f64())
                    .map(|f| rust_decimal::Decimal::from_f64_retain(f).unwrap_or_default())
                    .unwrap_or_default(),
                take_profit: risk_obj.get("take_profit")
                    .and_then(|v| v.as_f64())
                    .map(|f| rust_decimal::Decimal::from_f64_retain(f).unwrap_or_default())
                    .unwrap_or_default(),
                max_position_size: risk_obj.get("max_position_size")
                    .and_then(|v| v.as_f64())
                    .map(|f| rust_decimal::Decimal::from_f64_retain(f).unwrap_or_default())
                    .unwrap_or_default(),
                max_daily_trades: risk_obj.get("max_daily_trades")
                    .and_then(|v| v.as_u64())
                    .unwrap_or(100) as u32,
            })
        } else {
            Ok(RiskManagementConfig::default())
        }
    }

    /// åŸ·è¡Œæ¨¡æ¿åƒæ•¸æ›¿æ›
    pub fn substitute_template_parameters(&self, json_content: &str, parameters: &HashMap<String, Value>) -> String {
        let mut result = json_content.to_string();

        for (key, value) in parameters {
            let template_key = format!("{{{{{}}}}}", key);
            let replacement = match value {
                Value::String(s) => s.clone(),
                Value::Number(n) => n.to_string(),
                Value::Bool(b) => b.to_string(),
                _ => value.to_string(),
            };
            result = result.replace(&template_key, &replacement);
        }

        result
    }
}

