use rust_decimal::Decimal;
use std::collections::HashMap;

/// æŒ‡æ¨™å€¼é¡å‹
#[derive(Debug, Clone)]
pub enum IndicatorValue {
    Single(Decimal),
    Multiple(Vec<Decimal>),
}

/// æŒ‡æ¨™ç®¡ç†å™¨
pub struct IndicatorManager {
    indicators: HashMap<String, Box<dyn Indicator>>,
    values: HashMap<String, IndicatorValue>,
}

impl IndicatorManager {
    pub fn new() -> Self {
        Self {
            indicators: HashMap::new(),
            values: HashMap::new(),
        }
    }

    /// è¨»å†ŠæŒ‡æ¨™
    pub fn register_indicator(&mut self, name: String, indicator: Box<dyn Indicator>) {
        self.indicators.insert(name, indicator);
    }

    /// æ›´æ–°æŒ‡æ¨™å€¼
    pub fn update(&mut self, price: Decimal) {
        for (name, indicator) in &mut self.indicators {
            let value = indicator.calculate(price);
            self.values.insert(name.clone(), value);
        }
    }

    /// ç²å–æŒ‡æ¨™å€¼
    pub fn get_value(&self, name: &str) -> Option<&IndicatorValue> {
        self.values.get(name)
    }
}

/// æŒ‡æ¨™ç‰¹æ€§å®šç¾©
pub trait Indicator: Send + Sync {
    /// è¨ˆç®—æŒ‡æ¨™å€¼
    fn calculate(&mut self, price: Decimal) -> IndicatorValue;

    /// é‡ç½®æŒ‡æ¨™ç‹€æ…‹
    fn reset(&mut self);

    /// ç²å–æŒ‡æ¨™åç¨±
    fn name(&self) -> &str;
}

/// ç°¡å–®ç§»å‹•å¹³å‡ç·šæŒ‡æ¨™
pub struct SimpleMovingAverage {
    name: String,
    period: usize,
    prices: Vec<Decimal>,
}

impl SimpleMovingAverage {
    pub fn new(name: String, period: usize) -> Self {
        Self {
            name,
            period,
            prices: Vec::new(),
        }
    }
}

impl Indicator for SimpleMovingAverage {
    fn calculate(&mut self, price: Decimal) -> IndicatorValue {
        self.prices.push(price);

        if self.prices.len() > self.period {
            self.prices.remove(0);
        }

        if self.prices.len() == self.period {
            let sum: Decimal = self.prices.iter().sum();
            let avg = sum / Decimal::from(self.period);
            IndicatorValue::Single(avg)
        } else {
            IndicatorValue::Single(Decimal::ZERO)
        }
    }

    fn reset(&mut self) {
        self.prices.clear();
    }

    fn name(&self) -> &str {
        &self.name
    }
}

    /// ç”ŸæˆJSONæ ¼å¼å ±å‘Š
    pub fn generate_json_report(result: &BacktestResult) -> Result<String> {
        let json_report = serde_json::to_string_pretty(result)?;
        Ok(json_report)
    }

    /// ç”ŸæˆCSVæ ¼å¼äº¤æ˜“è¨˜éŒ„
    pub fn generate_trades_csv(result: &BacktestResult) -> Result<String> {
        let mut csv_content = String::from("trade_id,symbol,entry_time,exit_time,entry_price,exit_price,side,quantity,pnl,commission,strategy\n");

        for trade in &result.trades {
            let exit_time = trade.exit_time
                .map(|t| t.format("%Y-%m-%d %H:%M:%S").to_string())
                .unwrap_or_else(|| "N/A".to_string());
            let exit_price = trade.exit_price
                .map(|p| p.to_string())
                .unwrap_or_else(|| "N/A".to_string());
            let pnl = trade.pnl
                .map(|p| p.to_string())
                .unwrap_or_else(|| "N/A".to_string());

            csv_content.push_str(&format!(
                "{},{},{},{},{},{},{:?},{},{},{},{}\n",
                trade.id,
                trade.symbol,
                trade.entry_time.format("%Y-%m-%d %H:%M:%S"),
                exit_time,
                trade.entry_price,
                exit_price,
                trade.side,
                trade.quantity,
                pnl,
                trade.commission,
                trade.strategy
            ));
        }

        Ok(csv_content)
    }

    /// è¨ˆç®—é¡å¤–çš„çµ±è¨ˆæŒ‡æ¨™
    pub fn calculate_additional_metrics(result: &BacktestResult) -> Result<serde_json::Value> {
        let total_trades = result.metrics.total_trades as f64;
        let winning_trades = result.metrics.winning_trades as f64;
        let losing_trades = result.metrics.losing_trades as f64;

        let additional_metrics = serde_json::json!({
            "profit_loss_ratio": if losing_trades > 0.0 { 
                result.metrics.avg_win / result.metrics.avg_loss 
            } else { 
                rust_decimal::Decimal::ZERO 
            },
            "recovery_factor": if result.metrics.max_drawdown != rust_decimal::Decimal::ZERO {
                result.metrics.total_return / result.metrics.max_drawdown.abs()
            } else {
                rust_decimal::Decimal::ZERO
            },
            "trade_frequency": total_trades / 252.0, // å‡è¨­252å€‹äº¤æ˜“æ—¥
        });

        Ok(additional_metrics)
    }
}

