pub mod binance;
pub mod bybit;
pub mod okx;

pub use binance::BinanceAdapter;
pub use bybit::BybitAdapter;
pub use okx::OKXAdapter;

use crate::{Balance, MarketData, Order, OrderResponse, OrderUpdate, Position};
use async_trait::async_trait;
use futures_util::Stream;
use std::pin::Pin;
use thiserror::Error;

/// 交易所適配器錯誤類型
#[derive(Error, Debug)]
pub enum ExchangeError {
    #[error("API 錯誤: {0}")]
    ApiError(String),

    #[error("認證錯誤: {0}")]
    AuthError(String),

    #[error("網路連接錯誤: {0}")]
    NetworkError(String),

    #[error("JSON 解析錯誤: {0}")]
    ParseError(#[from] serde_json::Error),

    #[error("HTTP 錯誤: {0}")]
    HttpError(#[from] reqwest::Error),

    #[error("WebSocket 錯誤: {0}")]
    WebSocketError(String),

    #[error("速率限制錯誤: {message}")]
    RateLimitError {
        retry_after: Option<u64>,
        message: String,
    },

    #[error("訂單不存在: {order_id}")]
    OrderNotFound { order_id: String },

    #[error("餘額不足: 需要 {required}，可用 {available}")]
    InsufficientBalance { required: String, available: String },

    #[error("交易對不支援: {symbol}")]
    UnsupportedSymbol { symbol: String },

    #[error("數據庫錯誤: {0}")]
    DatabaseError(String),

    #[error("配置錯誤: {0}")]
    ConfigError(String),

    #[error("數據轉換錯誤: {0}")]
    DataError(String),

    #[error("服務錯誤: {0}")]
    ServiceError(String),

    #[error("未知錯誤: {0}")]
    Unknown(String),
}

pub type ExchangeResult<T> = Result<T, ExchangeError>;

/// 市場數據流類型
pub type MarketDataStream = Pin<Box<dyn Stream<Item = Result<MarketData, ExchangeError>> + Send>>;

/// 訂單更新流類型
pub type OrderUpdateStream = Pin<Box<dyn Stream<Item = Result<OrderUpdate, ExchangeError>> + Send>>;

/// 交易所適配器介面
#[async_trait]
pub trait ExchangeAdapter: Send + Sync {
    /// 獲取交易所名稱
    fn name(&self) -> &str;

    /// 下單
    async fn place_order(&self, order: &Order) -> ExchangeResult<OrderResponse>;

    /// 取消訂單
    async fn cancel_order(&self, order_id: &str) -> ExchangeResult<()>;

    /// 獲取訂單狀態
    async fn get_order_status(&self, order_id: &str) -> ExchangeResult<OrderResponse>;

    /// 獲取帳戶餘額
    async fn get_balance(&self) -> ExchangeResult<Vec<Balance>>;

    /// 獲取持倉資訊
    async fn get_positions(&self) -> ExchangeResult<Vec<Position>>;

    /// 訂閱市場數據
    async fn subscribe_market_data(&self, symbols: Vec<String>)
        -> ExchangeResult<MarketDataStream>;

    /// 訂閱訂單更新
    async fn subscribe_order_updates(&self) -> ExchangeResult<OrderUpdateStream>;

    /// 測試連接
    async fn test_connection(&self) -> ExchangeResult<bool>;

    /// 獲取伺服器時間
    async fn get_server_time(&self) -> ExchangeResult<chrono::DateTime<chrono::Utc>>;
}

/// WebSocket 連接管理器
pub struct WebSocketManager {
    connections: dashmap::DashMap<
        String,
        tokio_tungstenite::WebSocketStream<
            tokio_tungstenite::MaybeTlsStream<tokio::net::TcpStream>,
        >,
    >,
}

impl WebSocketManager {
    pub fn new() -> Self {
        Self {
            connections: dashmap::DashMap::new(),
        }
    }

    pub async fn connect(&self, url: &str) -> ExchangeResult<()> {
        let (ws_stream, _) = tokio_tungstenite::connect_async(url)
            .await
            .map_err(|e| ExchangeError::WebSocketError(format!("連接失敗: {e}")))?;

        self.connections.insert(url.to_string(), ws_stream);
        Ok(())
    }

    pub fn disconnect(&self, url: &str) {
        self.connections.remove(url);
    }

    pub fn is_connected(&self, url: &str) -> bool {
        self.connections.contains_key(url)
    }
}

impl Default for WebSocketManager {
    fn default() -> Self {
        Self::new()
    }
}

/// REST API 客戶端基礎功能
pub struct RestClient {
    client: reqwest::Client,
    base_url: String,
    api_key: Option<String>,
    secret_key: Option<String>,
}

/// 自定義請求構建器，支援交易所特定的認證機制
pub trait RequestBuilder: Send + Sync {
    fn build_request(
        &self,
        client: &reqwest::Client,
        method: &str,
        url: &str,
        body: Option<String>,
    ) -> Result<reqwest::RequestBuilder, ExchangeError>;
}

impl RestClient {
    pub fn new(base_url: String) -> Self {
        Self {
            client: reqwest::Client::new(),
            base_url,
            api_key: None,
            secret_key: None,
        }
    }

    pub fn with_credentials(mut self, api_key: String, secret_key: String) -> Self {
        self.api_key = Some(api_key);
        self.secret_key = Some(secret_key);
        self
    }

    /// 解析 HTTP 錯誤響應
    fn parse_error_response(status: reqwest::StatusCode, text: &str) -> ExchangeError {
        match status.as_u16() {
            401 | 403 => ExchangeError::AuthError("認證失敗".to_string()),
            429 => ExchangeError::RateLimitError {
                retry_after: None,
                message: "請求頻率過高".to_string(),
            },
            _ => ExchangeError::ApiError(format!("HTTP {}: {}", status, text)),
        }
    }

    /// 使用自定義請求構建器發送 GET 請求
    pub async fn get_with_builder<T: RequestBuilder>(
        &self,
        endpoint: &str,
        builder: &T,
    ) -> ExchangeResult<reqwest::Response> {
        let url = format!(
            "{}/{}",
            self.base_url,
            endpoint.strip_prefix('/').unwrap_or(endpoint)
        );

        let request = builder.build_request(&self.client, "GET", &url, None)?;
        let response = request.send().await?;

        if response.status().is_success() {
            Ok(response)
        } else {
            let status = response.status();
            let text = response.text().await.unwrap_or_default();
            Err(Self::parse_error_response(status, &text))
        }
    }

    /// 使用自定義請求構建器發送 POST 請求
    pub async fn post_with_builder<T: RequestBuilder>(
        &self,
        endpoint: &str,
        body: String,
        builder: &T,
    ) -> ExchangeResult<reqwest::Response> {
        let url = format!(
            "{}/{}",
            self.base_url,
            endpoint.strip_prefix('/').unwrap_or(endpoint)
        );

        let request = builder.build_request(&self.client, "POST", &url, Some(body))?;
        let response = request.send().await?;

        if response.status().is_success() {
            Ok(response)
        } else {
            let status = response.status();
            let text = response.text().await.unwrap_or_default();
            Err(Self::parse_error_response(status, &text))
        }
    }

    /// 使用自定義請求構建器發送 DELETE 請求
    pub async fn delete_with_builder<T: RequestBuilder>(
        &self,
        endpoint: &str,
        builder: &T,
    ) -> ExchangeResult<reqwest::Response> {
        let url = format!(
            "{}/{}",
            self.base_url,
            endpoint.strip_prefix('/').unwrap_or(endpoint)
        );

        let request = builder.build_request(&self.client, "DELETE", &url, None)?;
        let response = request.send().await?;

        if response.status().is_success() {
            Ok(response)
        } else {
            let status = response.status();
            let text = response.text().await.unwrap_or_default();
            Err(Self::parse_error_response(status, &text))
        }
    }

    pub async fn get(&self, endpoint: &str) -> ExchangeResult<reqwest::Response> {
        let url = format!(
            "{}/{}",
            self.base_url,
            endpoint.strip_prefix('/').unwrap_or(endpoint)
        );

        let mut request = self.client.get(&url);

        if let Some(api_key) = &self.api_key {
            request = request.header("X-API-KEY", api_key);
        }

        let response = request.send().await?;

        if response.status().is_success() {
            Ok(response)
        } else {
            Err(ExchangeError::ApiError(format!(
                "HTTP {}: {}",
                response.status(),
                response.text().await.unwrap_or_default()
            )))
        }
    }

    pub async fn post<T: serde::Serialize>(
        &self,
        endpoint: &str,
        body: &T,
    ) -> ExchangeResult<reqwest::Response> {
        let url = format!(
            "{}/{}",
            self.base_url,
            endpoint.strip_prefix('/').unwrap_or(endpoint)
        );

        let mut request = self.client.post(&url).json(body);

        if let Some(api_key) = &self.api_key {
            request = request.header("X-API-KEY", api_key);
        }

        let response = request.send().await?;

        if response.status().is_success() {
            Ok(response)
        } else {
            Err(ExchangeError::ApiError(format!(
                "HTTP {}: {}",
                response.status(),
                response.text().await.unwrap_or_default()
            )))
        }
    }

    pub async fn delete(&self, endpoint: &str) -> ExchangeResult<reqwest::Response> {
        let url = format!(
            "{}/{}",
            self.base_url,
            endpoint.strip_prefix('/').unwrap_or(endpoint)
        );

        let mut request = self.client.delete(&url);

        if let Some(api_key) = &self.api_key {
            request = request.header("X-API-KEY", api_key);
        }

        let response = request.send().await?;

        if response.status().is_success() {
            Ok(response)
        } else {
            Err(ExchangeError::ApiError(format!(
                "HTTP {}: {}",
                response.status(),
                response.text().await.unwrap_or_default()
            )))
        }
    }
}

