    /// 生成 HMAC-SHA256 簽名
    fn generate_signature(&self, query_string: &str) -> String {
        use hmac::{Hmac, Mac};
        use sha2::Sha256;

        type HmacSha256 = Hmac<Sha256>;

        let mut mac = HmacSha256::new_from_slice(self.secret_key.as_bytes())
            .expect("HMAC can take key of any size");
        mac.update(query_string.as_bytes());
        hex::encode(mac.finalize().into_bytes())
    }

    /// 轉換訂單方向到 Binance 格式
    fn convert_order_side(side: &OrderSide) -> String {
        match side {
            OrderSide::Buy => "BUY".to_string(),
            OrderSide::Sell => "SELL".to_string(),
        }
    }

    /// 轉換訂單類型到 Binance 格式
    fn convert_order_type(order_type: &OrderType) -> String {
        match order_type {
            OrderType::Market => "MARKET".to_string(),
            OrderType::Limit => "LIMIT".to_string(),
            OrderType::StopMarket => "STOP_MARKET".to_string(),
            OrderType::StopLimit => "STOP_LIMIT".to_string(),
        }
    }

    /// 轉換 Binance 訂單狀態到內部格式
    fn convert_order_status(status: &str) -> OrderStatus {
        match status {
            "NEW" => OrderStatus::New,
            "PARTIALLY_FILLED" => OrderStatus::PartiallyFilled,
            "FILLED" => OrderStatus::Filled,
            "CANCELED" => OrderStatus::Cancelled,
            "REJECTED" => OrderStatus::Rejected,
            _ => OrderStatus::New,
        }
    }

    /// 構建認證查詢字符串
    fn build_signed_query(&self, params: &[(&str, &str)]) -> String {
        let timestamp = chrono::Utc::now().timestamp_millis();
        let timestamp_str = timestamp.to_string();
        let mut query_params = params.to_vec();
        query_params.push(("timestamp", &timestamp_str));

        let query_string = query_params
            .iter()
            .map(|(k, v)| format!("{k}={v}"))
            .collect::<Vec<_>>()
            .join("&");

        let signature = self.generate_signature(&query_string);
        format!("{query_string}&signature={signature}")
    }

    /// 獲取 WebSocket 流 URL
    fn get_ws_url(&self) -> String {
        if self.is_testnet {
            "wss://testnet.binance.vision/ws".to_string()
        } else {
            "wss://stream.binance.com:9443/ws".to_string()
        }
    }

    /// 解析十進制數字，處理解析錯誤
    fn parse_decimal(s: &str) -> ExchangeResult<Decimal> {
        Decimal::from_str(s).map_err(|_| ExchangeError::Unknown(format!("無法解析十進制數字: {s}")))
    }
}

