use crate::types::*;
use crate::error::{Result, TradingError};
use crate::exchange::{ExchangeAdapter, ExchangeResult};
use async_trait::async_trait;
use dashmap::DashMap;
use std::sync::Arc;
use tokio::sync::broadcast;
use tracing::{info, warn, error};
use uuid::Uuid;

/// è¨‚å–®ç®¡ç†å™¨
/// è² è²¬ç®¡ç†å¤šå€‹äº¤æ˜“æ‰€çš„è¨‚å–®è·¯ç”±ã€ç‹€æ…‹è¿½è¹¤å’Œæ›´æ–°æ©Ÿåˆ¶
pub struct OrderManager {
    /// äº¤æ˜“æ‰€é©é…å™¨æ˜ å°„ (exchange_name -> adapter)
    exchanges: DashMap<String, Arc<dyn ExchangeAdapter>>,
    /// å¾…è™•ç†è¨‚å–®æ˜ å°„ (order_id -> order)
    pending_orders: DashMap<String, Order>,
    /// è¨‚å–®ç‹€æ…‹æ˜ å°„ (order_id -> status)
    order_status: DashMap<String, OrderStatus>,
    /// è¨‚å–®æ›´æ–°äº‹ä»¶ç™¼é€å™¨
    order_updates_tx: broadcast::Sender<OrderUpdate>,
}

impl OrderManager {
    /// å‰µå»ºæ–°çš„è¨‚å–®ç®¡ç†å™¨
    pub fn new() -> Self {
        let (order_updates_tx, _) = broadcast::channel(1000);

        Self {
            exchanges: DashMap::new(),
            pending_orders: DashMap::new(),
            order_status: DashMap::new(),
            order_updates_tx,
        }
    }

    /// æ·»åŠ äº¤æ˜“æ‰€é©é…å™¨
    pub fn add_exchange(&self, adapter: Arc<dyn ExchangeAdapter>) {
        let exchange_name = adapter.name().to_string();
        info!("æ·»åŠ äº¤æ˜“æ‰€é©é…å™¨: {}", exchange_name);
        self.exchanges.insert(exchange_name, adapter);
    }

    /// ç§»é™¤äº¤æ˜“æ‰€é©é…å™¨
    pub fn remove_exchange(&self, exchange_name: &str) -> Option<Arc<dyn ExchangeAdapter>> {
        info!("ç§»é™¤äº¤æ˜“æ‰€é©é…å™¨: {}", exchange_name);
        self.exchanges.remove(exchange_name).map(|(_, adapter)| adapter)
    }

    /// ç²å–è¨‚å–®æ›´æ–°äº‹ä»¶æ¥æ”¶å™¨
    pub fn subscribe_order_updates(&self) -> broadcast::Receiver<OrderUpdate> {
        self.order_updates_tx.subscribe()
    }    /// ä¸‹å–®
    /// æ ¹æ“šè¨‚å–®ä¸­çš„äº¤æ˜“æ‰€ä¿¡æ¯è·¯ç”±åˆ°å°æ‡‰çš„é©é…å™¨
    pub async fn place_order(&self, mut order: Order) -> Result<OrderResponse> {
        // å¦‚æœè¨‚å–®æ²’æœ‰IDï¼Œç”Ÿæˆä¸€å€‹
        if order.id.is_empty() {
            order.id = Uuid::new_v4().to_string();
        }

        // ç²å–å°æ‡‰çš„äº¤æ˜“æ‰€é©é…å™¨
        let adapter = self.exchanges.get(&order.exchange)
            .ok_or_else(|| TradingError::ExchangeNotFound(order.exchange.clone()))?;

        info!("ä¸‹å–®åˆ°äº¤æ˜“æ‰€ {} - è¨‚å–®ID: {}", order.exchange, order.id);

        // è¨˜éŒ„è¨‚å–®ç‚ºå¾…è™•ç†ç‹€æ…‹
        self.pending_orders.insert(order.id.clone(), order.clone());
        self.order_status.insert(order.id.clone(), OrderStatus::New);

        // åŸ·è¡Œä¸‹å–®
        match adapter.place_order(&order).await {
            Ok(response) => {
                info!("è¨‚å–®æäº¤æˆåŠŸ: {} -> {}", order.id, response.order_id);

                // æ›´æ–°è¨‚å–®ç‹€æ…‹
                self.update_order_status(&order.id, response.status.clone()).await;

                Ok(response)
            },
            Err(e) => {
                error!("è¨‚å–®æäº¤å¤±æ•—: {} - {}", order.id, e);

                // æ›´æ–°ç‚ºæ‹’çµ•ç‹€æ…‹
                self.update_order_status(&order.id, OrderStatus::Rejected).await;

                Err(e.into())
            }
        }
    }    /// æ’¤å–®
    pub async fn cancel_order(&self, order_id: &str) -> Result<()> {
        // ç²å–è¨‚å–®ä¿¡æ¯
        let order = self.pending_orders.get(order_id)
            .ok_or_else(|| TradingError::OrderNotFound(order_id.to_string()))?;

        // ç²å–å°æ‡‰çš„äº¤æ˜“æ‰€é©é…å™¨
        let adapter = self.exchanges.get(&order.exchange)
            .ok_or_else(|| TradingError::ExchangeNotFound(order.exchange.clone()))?;

        info!("æ’¤éŠ·è¨‚å–®: {} å¾äº¤æ˜“æ‰€ {}", order_id, order.exchange);

        // åŸ·è¡Œæ’¤å–®
        match adapter.cancel_order(order_id).await {
            Ok(_) => {
                info!("æ’¤å–®æˆåŠŸ: {}", order_id);
                self.update_order_status(order_id, OrderStatus::Cancelled).await;
                Ok(())
            },
            Err(e) => {
                error!("æ’¤å–®å¤±æ•—: {} - {}", order_id, e);
                Err(e.into())
            }
        }
    }

    /// ç²å–è¨‚å–®ç‹€æ…‹
    pub fn get_order_status(&self, order_id: &str) -> Option<OrderStatus> {
        self.order_status.get(order_id).map(|status| status.clone())
    }

    /// ç²å–å¾…è™•ç†è¨‚å–®
    pub fn get_pending_order(&self, order_id: &str) -> Option<Order> {
        self.pending_orders.get(order_id).map(|order| order.clone())
    }    /// è™•ç†è¨‚å–®æ›´æ–°
    /// é€™å€‹æ–¹æ³•é€šå¸¸ç”±äº¤æ˜“æ‰€é©é…å™¨çš„äº‹ä»¶å›èª¿èª¿ç”¨
    pub async fn handle_order_update(&self, update: OrderUpdate) {
        info!("æ”¶åˆ°è¨‚å–®æ›´æ–°: {} -> {:?}", update.order_id, update.status);

        // æ›´æ–°è¨‚å–®ç‹€æ…‹
        self.order_status.insert(update.order_id.clone(), update.status.clone());

        // å¦‚æœè¨‚å–®å·²å®Œæˆï¼ˆæˆäº¤æˆ–å–æ¶ˆï¼‰ï¼Œå¾å¾…è™•ç†åˆ—è¡¨ä¸­ç§»é™¤
        match update.status {
            OrderStatus::Filled | OrderStatus::Cancelled | OrderStatus::Rejected => {
                if let Some((_, _removed_order)) = self.pending_orders.remove(&update.order_id) {
                    info!("è¨‚å–® {} å·²å®Œæˆï¼Œç‹€æ…‹: {:?}", update.order_id, update.status);
                }
            },
            _ => {
                // éƒ¨åˆ†æˆäº¤ç­‰ç‹€æ…‹ä¿ç•™åœ¨å¾…è™•ç†åˆ—è¡¨ä¸­
            }
        }

        // å»£æ’­è¨‚å–®æ›´æ–°äº‹ä»¶
        if let Err(e) = self.order_updates_tx.send(update) {
            warn!("å»£æ’­è¨‚å–®æ›´æ–°å¤±æ•—: {}", e);
        }
    }

    /// å…§éƒ¨æ–¹æ³•ï¼šæ›´æ–°è¨‚å–®ç‹€æ…‹ä¸¦å»£æ’­äº‹ä»¶
    async fn update_order_status(&self, order_id: &str, status: OrderStatus) {
        let update = OrderUpdate {
            order_id: order_id.to_string(),
            status,
            filled_quantity: rust_decimal::Decimal::ZERO, // å¯¦éš›æ‡‰ç”¨ä¸­éœ€è¦å¾äº¤æ˜“æ‰€ç²å–
            avg_price: None, // å¯¦éš›æ‡‰ç”¨ä¸­éœ€è¦å¾äº¤æ˜“æ‰€ç²å–
            timestamp: chrono::Utc::now(),
        };

        self.handle_order_update(update).await;
    }    /// ç²å–æ‰€æœ‰å¾…è™•ç†è¨‚å–®
    pub fn get_all_pending_orders(&self) -> Vec<Order> {
        self.pending_orders.iter().map(|entry| entry.value().clone()).collect()
    }

    /// ç²å–æŒ‡å®šäº¤æ˜“æ‰€çš„å¾…è™•ç†è¨‚å–®
    pub fn get_pending_orders_by_exchange(&self, exchange: &str) -> Vec<Order> {
        self.pending_orders
            .iter()
            .filter(|entry| entry.value().exchange == exchange)
            .map(|entry| entry.value().clone())
            .collect()
    }

    /// ç²å–å·²è¨»å†Šçš„äº¤æ˜“æ‰€åˆ—è¡¨
    pub fn get_registered_exchanges(&self) -> Vec<String> {
        self.exchanges.iter().map(|entry| entry.key().clone()).collect()
    }

    /// ç²å–è¨‚å–®çµ±è¨ˆä¿¡æ¯
    pub fn get_order_stats(&self) -> OrderStats {
        let mut stats = OrderStats::default();

        for entry in self.order_status.iter() {
            match entry.value() {
                OrderStatus::New => stats.new_count += 1,
                OrderStatus::PartiallyFilled => stats.partially_filled_count += 1,
                OrderStatus::Filled => stats.filled_count += 1,
                OrderStatus::Cancelled => stats.cancelled_count += 1,
                OrderStatus::Rejected => stats.rejected_count += 1,
            }
        }

        stats.total_count = stats.new_count + stats.partially_filled_count + 
                           stats.filled_count + stats.cancelled_count + stats.rejected_count;

        stats
    }
}/// è¨‚å–®çµ±è¨ˆä¿¡æ¯
