use thiserror::Error;
use crate::exchange::ExchangeError;

/// äº¤æ˜“ç³»çµ±éŒ¯èª¤é¡å‹
#[derive(Debug, Error)]
pub enum TradingError {
    #[error("äº¤æ˜“æ‰€é€£æ¥éŒ¯èª¤: {0}")]
    ExchangeConnectionError(String),

    #[error("è¨‚å–®åŸ·è¡Œå¤±æ•—: {0}")]
    OrderExecutionError(String),

    #[error("é¢¨éšªæ§åˆ¶æ‹’çµ•: {0}")]
    RiskControlError(String),

    #[error("ç­–ç•¥è¼‰å…¥éŒ¯èª¤: {0}")]
    StrategyLoadError(String),

    #[error("æ•¸æ“šéŒ¯èª¤: {0}")]
    DataError(String),

    #[error("é…ç½®éŒ¯èª¤: {0}")]
    ConfigError(String),

    #[error("ç¶²çµ¡éŒ¯èª¤: {0}")]
    NetworkError(String),

    #[error("è§£æéŒ¯èª¤: {0}")]
    ParseError(String),

    #[error("äº¤æ˜“æ‰€æœªæ‰¾åˆ°: {0}")]
    ExchangeNotFound(String),

    #[error("è¨‚å–®æœªæ‰¾åˆ°: {0}")]
    OrderNotFound(String),
}

/// å¾ ExchangeError è½‰æ›åˆ° TradingError
impl From<ExchangeError> for TradingError {
    fn from(err: ExchangeError) -> Self {
        match err {
            ExchangeError::ApiError(msg) => TradingError::OrderExecutionError(msg),
            ExchangeError::AuthError(msg) => TradingError::ConfigError(msg),
            ExchangeError::NetworkError(msg) => TradingError::NetworkError(msg),
            ExchangeError::ParseError(e) => TradingError::ParseError(e.to_string()),
            ExchangeError::HttpError(e) => TradingError::NetworkError(e.to_string()),
            ExchangeError::WebSocketError(msg) => TradingError::ExchangeConnectionError(msg),
            ExchangeError::OrderNotFound { order_id } => TradingError::OrderNotFound(order_id),
            ExchangeError::InsufficientBalance { required, available } => {
                TradingError::RiskControlError(format!("é¤˜é¡ä¸è¶³: éœ€è¦ {}ï¼Œå¯ç”¨ {}", required, available))
            },
            ExchangeError::UnsupportedSymbol { symbol } => {
                TradingError::ConfigError(format!("ä¸æ”¯æ´çš„äº¤æ˜“å°: {}", symbol))
            },
            ExchangeError::Unknown(msg) => TradingError::OrderExecutionError(msg),
        }
    }
}

pub type Result<T> = std::result::Result<T, TradingError>;

