/// Bybit äº¤æ˜“æ‰€é©é…å™¨
pub struct BybitAdapter {
    #[allow(dead_code)]
    rest_client: RestClient,
    #[allow(dead_code)]
    ws_manager: WebSocketManager,
    api_key: String,
    secret_key: String,
}

impl BybitAdapter {
    pub fn new(api_key: String, secret_key: String) -> Self {
        Self {
            rest_client: RestClient::new("https://api.bybit.com".to_string())
                .with_credentials(api_key.clone(), secret_key.clone()),
            ws_manager: WebSocketManager::new(),
            api_key,
            secret_key,
        }
    }

    pub fn testnet(api_key: String, secret_key: String) -> Self {
        Self {
            rest_client: RestClient::new("https://api-testnet.bybit.com".to_string())
                .with_credentials(api_key.clone(), secret_key.clone()),
            ws_manager: WebSocketManager::new(),
            api_key,
            secret_key,
        }
    }

    /// ç”Ÿæˆ API ç°½å
    fn generate_signature(&self, timestamp: i64, params: &str) -> String {
        use hmac::{Hmac, Mac};
        use sha2::Sha256;

        type HmacSha256 = Hmac<Sha256>;

        let message = format!("{}{}{}", timestamp, &self.api_key, params);
        let mut mac = HmacSha256::new_from_slice(self.secret_key.as_bytes()).unwrap();
        mac.update(message.as_bytes());
        hex::encode(mac.finalize().into_bytes())
    }

    /// ç”¢ç”Ÿå®¢æˆ¶ç«¯è¨‚å–® ID
    #[allow(dead_code)]
    fn generate_client_order_id() -> String {
        use std::time::{SystemTime, UNIX_EPOCH};
        let timestamp = SystemTime::now()
            .duration_since(UNIX_EPOCH)
            .unwrap()
            .as_millis();
        format!("bybit_{}", timestamp)
    }

    /// è½‰æ›è¨‚å–®æ–¹å‘
    fn convert_order_side(side: &OrderSide) -> String {
        match side {
            OrderSide::Buy => "Buy".to_string(),
            OrderSide::Sell => "Sell".to_string(),
        }
    }

    /// è½‰æ›è¨‚å–®é¡å‹
    fn convert_order_type(order_type: &OrderType) -> String {
        match order_type {
            OrderType::Market => "Market".to_string(),
            OrderType::Limit => "Limit".to_string(),
            OrderType::StopMarket => "STOP".to_string(),
            OrderType::StopLimit => "StopLimit".to_string(),
        }
    }

    /// è½‰æ›è¨‚å–®ç‹€æ…‹
    fn convert_order_status(status: &str) -> OrderStatus {
        match status {
            "New" => OrderStatus::New,
            "PartiallyFilled" => OrderStatus::PartiallyFilled,
            "Filled" => OrderStatus::Filled,
            "Cancelled" => OrderStatus::Cancelled,
            "Rejected" => OrderStatus::Rejected,
            _ => OrderStatus::New,
        }
    }

    /// å‰µå»ºèªè­‰æ¨™é ­
    fn create_auth_headers(&self, timestamp: i64, params: &str) -> std::collections::HashMap<String, String> {
        let recv_window = "5000"; // 5 ç§’æ¥æ”¶çª—å£
        let param_str = if params.is_empty() {
            format!("{}{}", timestamp, recv_window)
        } else {
            format!("{}{}{}", timestamp, self.api_key, params)
        };

        let mut headers = std::collections::HashMap::new();
        headers.insert("X-BAPI-API-KEY".to_string(), self.api_key.clone());
        headers.insert("X-BAPI-TIMESTAMP".to_string(), timestamp.to_string());
        headers.insert("X-BAPI-RECV-WINDOW".to_string(), recv_window.to_string());
        headers.insert("X-BAPI-SIGN".to_string(), self.generate_signature(timestamp, &param_str));
        headers.insert("Content-Type".to_string(), "application/json".to_string());
        headers
    }
}

#[async_trait]
impl ExchangeAdapter for BybitAdapter {
    fn name(&self) -> &str {
        "Bybit"
    }

    async fn place_order(&self, order: &Order) -> ExchangeResult<OrderResponse> {
        let order_request = BybitOrderRequest {
            category: "spot".to_string(), // ç¾è²¨äº¤æ˜“
            symbol: order.symbol.clone(),
            side: Self::convert_order_side(&order.side),
            order_type: Self::convert_order_type(&order.order_type),
            qty: order.quantity.to_string(),
            price: order.price.map(|p| p.to_string()),
            order_link_id: Some(order.id.clone()),
        };

        let body = serde_json::to_string(&order_request)
            .map_err(|e| ExchangeError::ParseError(e))?;

        let timestamp = chrono::Utc::now().timestamp_millis();
