                if let Ok(timestamp) = time_str.parse::<i64>() {
                    return Ok(DateTime::from_timestamp(timestamp, 0)
                        .unwrap_or_else(chrono::Utc::now));
                }
            }
        }

        Ok(chrono::Utc::now())
    }
