pub mod bot;
pub mod formatter;
pub mod notification_queue;
pub mod types;

pub use bot::DiscordBot;
pub use formatter::{MessageFormatter, TradingMessageFormatter};
pub use notification_queue::{NotificationQueue, NotificationQueueConfig};
pub use types::*;

use serde::{Deserialize, Serialize};
use chrono::{DateTime, Utc};
use std::fmt;

/// Discord notification types
#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum NotificationType {
    /// Trading execution notifications
    Trade,
    /// Risk management alerts
    Risk,
    /// System status updates
    System,
    /// Performance reports
    Performance,
    /// Error notifications
    Error,
}

/// Trading event types for notifications
#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum TradingEvent {
    OrderPlaced {
        symbol: String,
        side: String,
        quantity: String,
        price: String,
        order_id: String,
    },
    OrderFilled {
        symbol: String,
        side: String,
        quantity: String,
        price: String,
        order_id: String,
        commission: String,
    },
    OrderCancelled {
        symbol: String,
        side: String,
        quantity: String,
        price: String,
        order_id: String,
        reason: String,
    },
    PositionOpened {
        symbol: String,
        side: String,
        size: String,
        entry_price: String,
        unrealized_pnl: String,
    },
    PositionClosed {
        symbol: String,
        side: String,
        size: String,
        exit_price: String,
        realized_pnl: String,
        holding_time: String,
    },
    RiskAlert {
        level: RiskLevel,
        message: String,
        current_exposure: String,
        max_exposure: String,
    },
    SystemError {
        component: String,
        error_message: String,
        severity: ErrorSeverity,
    },
}

/// Risk alert levels
#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum RiskLevel {
    Low,
    Medium,
    High,
    Critical,
}

/// Error severity levels
#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum ErrorSeverity {
    Info,
    Warning,
    Error,
    Fatal,
}

/// Discord notification message
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct NotificationMessage {
    pub id: String,
    pub timestamp: DateTime<Utc>,
    pub notification_type: NotificationType,
    pub title: String,
    pub content: String,
    pub color: Option<u32>, // Discord embed color (hex)
    pub fields: Vec<MessageField>,
    pub priority: Priority,
    pub channel_id: Option<String>, // Target channel ID
}

/// Message field for Discord embeds
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct MessageField {
    pub name: String,
    pub value: String,
    pub inline: bool,
}

/// Message priority levels
#[derive(Debug, Clone, Serialize, Deserialize, PartialEq, Eq, PartialOrd, Ord)]
pub enum Priority {
    Low = 1,
    Normal = 2,
    High = 3,
    Critical = 4,
}

/// Discord bot configuration
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct DiscordConfig {
    pub token: String,
    pub default_channel_id: String,
    pub trading_channel_id: Option<String>,
    pub risk_channel_id: Option<String>,
    pub system_channel_id: Option<String>,
    pub enable_embeds: bool,
    pub max_retries: u32,
    pub retry_delay_seconds: u64,
}

impl fmt::Display for NotificationType {
    fn fmt(&self, f: &mut fmt::Formatter<'_>) -> fmt::Result {
        match self {
            NotificationType::Trade => write!(f, "Trade"),
            NotificationType::Risk => write!(f, "Risk"),
            NotificationType::System => write!(f, "System"),
            NotificationType::Performance => write!(f, "Performance"),
            NotificationType::Error => write!(f, "Error"),
        }
    }
}

impl fmt::Display for RiskLevel {
    fn fmt(&self, f: &mut fmt::Formatter<'_>) -> fmt::Result {
        match self {
            RiskLevel::Low => write!(f, "Low"),
            RiskLevel::Medium => write!(f, "Medium"),
            RiskLevel::High => write!(f, "High"),
            RiskLevel::Critical => write!(f, "Critical"),
        }
    }
}

impl fmt::Display for ErrorSeverity {
    fn fmt(&self, f: &mut fmt::Formatter<'_>) -> fmt::Result {
        match self {
            ErrorSeverity::Info => write!(f, "Info"),
            ErrorSeverity::Warning => write!(f, "Warning"),
            ErrorSeverity::Error => write!(f, "Error"),
            ErrorSeverity::Fatal => write!(f, "Fatal"),
        }
    }
}

impl fmt::Display for Priority {
    fn fmt(&self, f: &mut fmt::Formatter<'_>) -> fmt::Result {
        match self {
            Priority::Low => write!(f, "Low"),
            Priority::Normal => write!(f, "Normal"),
            Priority::High => write!(f, "High"),
            Priority::Critical => write!(f, "Critical"),
        }
    }
}

impl NotificationMessage {
    pub fn new(notification_type: NotificationType, title: String, content: String) -> Self {
        Self {
            id: uuid::Uuid::new_v4().to_string(),
            timestamp: Utc::now(),
            notification_type,
            title,
            content,
            color: None,
            fields: Vec::new(),
            priority: Priority::Normal,
            channel_id: None,
        }
    }

    pub fn with_color(mut self, color: u32) -> Self {
        self.color = Some(color);
        self
    }

    pub fn with_priority(mut self, priority: Priority) -> Self {
        self.priority = priority;
        self
    }

    pub fn with_channel(mut self, channel_id: String) -> Self {
        self.channel_id = Some(channel_id);
        self
    }

    pub fn add_field(mut self, name: String, value: String, inline: bool) -> Self {
        self.fields.push(MessageField { name, value, inline });
        self
    }
}

impl Default for DiscordConfig {
    fn default() -> Self {
        Self {
            token: String::new(),
            default_channel_id: String::new(),
            trading_channel_id: None,
            risk_channel_id: None,
            system_channel_id: None,
            enable_embeds: true,
            max_retries: 3,
            retry_delay_seconds: 5,
        }
    }
}

use crate::discord::types::{NotificationMessage, Priority};
use serde::{Deserialize, Serialize};
use std::collections::VecDeque;
use std::sync::Arc;
use tokio::sync::{Mutex, RwLock};
use tokio::time::{Duration, Instant};
use tracing::{debug, error, info, warn};

/// Configuration for the notification queue
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct NotificationQueueConfig {
    /// Maximum number of messages in the queue
    pub max_queue_size: usize,
    /// Batch processing size
    pub batch_size: usize,
    /// Processing interval in milliseconds
    pub processing_interval_ms: u64,
    /// Maximum retry attempts for failed messages
    pub max_retries: u32,
    /// Delay between retry attempts in milliseconds
    pub retry_delay_ms: u64,
    /// Rate limit: messages per minute
    pub rate_limit_per_minute: u32,
}

impl Default for NotificationQueueConfig {
    fn default() -> Self {
        Self {
            max_queue_size: 1000,
            batch_size: 10,
            processing_interval_ms: 1000,
            max_retries: 3,
            retry_delay_ms: 5000,
            rate_limit_per_minute: 60,
        }
    }
}

/// Internal queue item with retry logic
#[derive(Debug, Clone)]
struct QueueItem {
    message: NotificationMessage,
    retry_count: u32,
    last_attempt: Option<Instant>,
}

impl QueueItem {
    fn new(message: NotificationMessage) -> Self {
        Self {
            message,
            retry_count: 0,
            last_attempt: None,
        }
    }

    fn should_retry(&self, config: &NotificationQueueConfig) -> bool {
        self.retry_count < config.max_retries
    }

    fn can_retry_now(&self, config: &NotificationQueueConfig) -> bool {
        match self.last_attempt {
            Some(last) => {
                let elapsed = last.elapsed();
                elapsed.as_millis() >= config.retry_delay_ms as u128
            }
            None => true,
        }
    }

    fn increment_retry(&mut self) {
        self.retry_count += 1;
        self.last_attempt = Some(Instant::now());
    }
}

/// Rate limiting tracker
#[derive(Debug)]
struct RateLimiter {
    message_times: VecDeque<Instant>,
    limit_per_minute: u32,
}

impl RateLimiter {
    fn new(limit_per_minute: u32) -> Self {
        Self {
            message_times: VecDeque::new(),
            limit_per_minute,
        }
    }

    fn can_send_message(&mut self) -> bool {
        let now = Instant::now();
        let minute_ago = now - Duration::from_secs(60);

        // Remove old entries
        while let Some(&front_time) = self.message_times.front() {
            if front_time < minute_ago {
                self.message_times.pop_front();
            } else {
                break;
            }
        }

        // Check if we're under the limit
        self.message_times.len() < self.limit_per_minute as usize
    }

    fn record_message(&mut self) {
        self.message_times.push_back(Instant::now());
    }
}

/// Notification queue for batching and managing Discord messages
pub struct NotificationQueue {
    config: NotificationQueueConfig,
    queue: Arc<Mutex<VecDeque<QueueItem>>>,
    retry_queue: Arc<Mutex<VecDeque<QueueItem>>>,
    rate_limiter: Arc<Mutex<RateLimiter>>,
    is_running: Arc<RwLock<bool>>,
    stats: Arc<RwLock<QueueStats>>,
}

/// Queue statistics
#[derive(Debug, Default, Clone, Serialize, Deserialize)]
pub struct QueueStats {
    pub messages_queued: u64,
    pub messages_sent: u64,
    pub messages_failed: u64,
    pub messages_dropped: u64,
    pub current_queue_size: usize,
    pub current_retry_queue_size: usize,
    pub rate_limited_count: u64,
}

impl NotificationQueue {
    /// Create a new notification queue
    pub fn new(config: NotificationQueueConfig) -> Self {
        let rate_limiter = RateLimiter::new(config.rate_limit_per_minute);

        Self {
            config,
            queue: Arc::new(Mutex::new(VecDeque::new())),
            retry_queue: Arc::new(Mutex::new(VecDeque::new())),
            rate_limiter: Arc::new(Mutex::new(rate_limiter)),
            is_running: Arc::new(RwLock::new(false)),
            stats: Arc::new(RwLock::new(QueueStats::default())),
        }
    }

    /// Add a message to the queue
    pub async fn enqueue(&self, message: NotificationMessage) -> Result<(), String> {
        let mut queue = self.queue.lock().await;

        // Check queue size limit
        if queue.len() >= self.config.max_queue_size {
            // If queue is full, try to drop lower priority messages
            if let Some(dropped) = self.drop_lowest_priority(&mut queue).await {
                warn!("Queue full, dropped message: {}", dropped.message.id);
                let mut stats = self.stats.write().await;
                stats.messages_dropped += 1;
            } else {
                return Err(format!("Queue is full ({})", self.config.max_queue_size));
            }
        }

        queue.push_back(QueueItem::new(message));

        // Update stats
        let mut stats = self.stats.write().await;
        stats.messages_queued += 1;
        stats.current_queue_size = queue.len();

        debug!("Message queued, queue size: {}", queue.len());
        Ok(())
    }

    /// Start the queue processing loop
    pub async fn start_processing<F, Fut>(&self, sender_fn: F) -> Result<(), String>
    where
        F: Fn(NotificationMessage) -> Fut + Send + Sync + 'static,
        Fut: std::future::Future<Output = Result<(), String>> + Send,
    {
        {
            let mut is_running = self.is_running.write().await;
            if *is_running {
                return Err("Queue is already running".to_string());
            }
            *is_running = true;
        }

        info!("Starting notification queue processing");

        let queue = Arc::clone(&self.queue);
        let retry_queue = Arc::clone(&self.retry_queue);
        let rate_limiter = Arc::clone(&self.rate_limiter);
        let is_running = Arc::clone(&self.is_running);
        let stats = Arc::clone(&self.stats);
        let config = self.config.clone();

        tokio::spawn(async move {
            let mut interval = tokio::time::interval(Duration::from_millis(config.processing_interval_ms));

            while *is_running.read().await {
                interval.tick().await;

                // Process retry queue first
                Self::process_retry_queue(&retry_queue, &rate_limiter, &sender_fn, &config, &stats).await;

                // Process main queue
                Self::process_main_queue(&queue, &retry_queue, &rate_limiter, &sender_fn, &config, &stats).await;
            }

            info!("Notification queue processing stopped");
        });

        Ok(())
    }

    /// Stop the queue processing
    pub async fn stop_processing(&self) {
        let mut is_running = self.is_running.write().await;
        *is_running = false;
        info!("Stopping notification queue processing");
    }

    /// Get current queue statistics
    pub async fn get_stats(&self) -> QueueStats {
        let stats = self.stats.read().await;
        let mut stats_copy = stats.clone();

        // Update current sizes
        stats_copy.current_queue_size = self.queue.lock().await.len();
        stats_copy.current_retry_queue_size = self.retry_queue.lock().await.len();

        stats_copy
    }

    /// Clear all queues
    pub async fn clear(&self) {
        self.queue.lock().await.clear();
        self.retry_queue.lock().await.clear();

        let mut stats = self.stats.write().await;
        stats.current_queue_size = 0;
        stats.current_retry_queue_size = 0;

        info!("All queues cleared");
    }

    // Helper method to drop lowest priority message
    async fn drop_lowest_priority(&self, queue: &mut VecDeque<QueueItem>) -> Option<QueueItem> {
        if queue.is_empty() {
            return None;
        }

        // Find the message with lowest priority (and oldest timestamp for ties)
        let mut lowest_idx = 0;
        let mut lowest_priority = Priority::Critical;
        let mut oldest_time = queue[0].message.timestamp;

        for (i, item) in queue.iter().enumerate() {
            if item.message.priority < lowest_priority || 
               (item.message.priority == lowest_priority && item.message.timestamp < oldest_time) {
                lowest_idx = i;
                lowest_priority = item.message.priority;
                oldest_time = item.message.timestamp;
            }
        }

        queue.remove(lowest_idx)
    }

    // Process the main queue
    async fn process_main_queue<F, Fut>(
        queue: &Arc<Mutex<VecDeque<QueueItem>>>,
        retry_queue: &Arc<Mutex<VecDeque<QueueItem>>>,
        rate_limiter: &Arc<Mutex<RateLimiter>>,
        sender_fn: &F,
        config: &NotificationQueueConfig,
        stats: &Arc<RwLock<QueueStats>>,
    ) where
        F: Fn(NotificationMessage) -> Fut + Send + Sync,
        Fut: std::future::Future<Output = Result<(), String>> + Send,
    {
        let mut items_to_process = Vec::new();

        // Extract items to process
        {
            let mut queue_guard = queue.lock().await;
            let batch_size = std::cmp::min(config.batch_size, queue_guard.len());

            for _ in 0..batch_size {
                if let Some(item) = queue_guard.pop_front() {
                    items_to_process.push(item);
                }
            }
        }

        // Process items
        for item in items_to_process {
            // Check rate limit
            if !rate_limiter.lock().await.can_send_message() {
                // Put back in queue and break
                queue.lock().await.push_front(item);
                let mut stats_guard = stats.write().await;
                stats_guard.rate_limited_count += 1;
                debug!("Rate limit reached, delaying processing");
                break;
            }

            // Try to send message
            match sender_fn(item.message.clone()).await {
                Ok(_) => {
                    rate_limiter.lock().await.record_message();
                    let mut stats_guard = stats.write().await;
                    stats_guard.messages_sent += 1;
                    debug!("Message sent successfully: {}", item.message.id);
                }
                Err(error) => {
                    error!("Failed to send message {}: {}", item.message.id, error);

                    let mut failed_item = item;
                    failed_item.increment_retry();

                    if failed_item.should_retry(config) {
                        retry_queue.lock().await.push_back(failed_item);
                        debug!("Message queued for retry: {}", failed_item.message.id);
                    } else {
                        let mut stats_guard = stats.write().await;
                        stats_guard.messages_failed += 1;
                        warn!("Message failed permanently: {}", failed_item.message.id);
                    }
                }
            }
        }
    }

    // Process the retry queue
    async fn process_retry_queue<F, Fut>(
        retry_queue: &Arc<Mutex<VecDeque<QueueItem>>>,
        rate_limiter: &Arc<Mutex<RateLimiter>>,
        sender_fn: &F,
        config: &NotificationQueueConfig,
        stats: &Arc<RwLock<QueueStats>>,
    ) where
        F: Fn(NotificationMessage) -> Fut + Send + Sync,
        Fut: std::future::Future<Output = Result<(), String>> + Send,
    {
        let mut items_to_retry = Vec::new();

        // Extract items ready for retry
        {
            let mut retry_guard = retry_queue.lock().await;
            let mut remaining_items = VecDeque::new();

            while let Some(item) = retry_guard.pop_front() {
                if item.can_retry_now(config) {
                    items_to_retry.push(item);
                } else {
                    remaining_items.push_back(item);
                }
            }

            *retry_guard = remaining_items;
        }

        // Process retry items
        for item in items_to_retry {
            // Check rate limit
            if !rate_limiter.lock().await.can_send_message() {
                // Put back in retry queue
                retry_queue.lock().await.push_front(item);
                break;
            }

            // Try to send message
            match sender_fn(item.message.clone()).await {
                Ok(_) => {
                    rate_limiter.lock().await.record_message();
                    let mut stats_guard = stats.write().await;
                    stats_guard.messages_sent += 1;
                    debug!("Retry message sent successfully: {}", item.message.id);
                }
                Err(error) => {
                    error!("Retry failed for message {}: {}", item.message.id, error);

                    let mut failed_item = item;
                    failed_item.increment_retry();

                    if failed_item.should_retry(config) {
                        retry_queue.lock().await.push_back(failed_item);
                        debug!("Message re-queued for retry: {}", failed_item.message.id);
                    } else {
                        let mut stats_guard = stats.write().await;
                        stats_guard.messages_failed += 1;
                        warn!("Retry message failed permanently: {}", failed_item.message.id);
                    }
                }
            }
        }
    }
}

use crate::discord::types::{
    NotificationMessage, NotificationType, TradingEvent, MessageField,
    RiskLevel, ErrorSeverity, Priority
};
use chrono::{DateTime, Utc};
use rust_decimal::Decimal;

/// Trait for formatting different types of messages
pub trait MessageFormatter {
    /// Format a trading event into a notification message
    fn format_trading_event(&self, event: TradingEvent) -> NotificationMessage;

    /// Format a system message
    fn format_system_message(&self, title: String, content: String, severity: ErrorSeverity) -> NotificationMessage;

    /// Format a performance report
    fn format_performance_report(&self, report: PerformanceReport) -> NotificationMessage;
}

/// Performance report data structure
#[derive(Debug, Clone)]
pub struct PerformanceReport {
    pub period: String,
    pub total_trades: u32,
    pub winning_trades: u32,
    pub losing_trades: u32,
    pub total_pnl: Decimal,
    pub win_rate: f64,
    pub average_win: Decimal,
    pub average_loss: Decimal,
    pub max_drawdown: Decimal,
    pub sharpe_ratio: Option<f64>,
    pub portfolio_value: Decimal,
}

/// Trading-specific message formatter
pub struct TradingMessageFormatter {
    default_channel: Option<String>,
}

impl TradingMessageFormatter {
    pub fn new(default_channel: Option<String>) -> Self {
        Self { default_channel }
    }

    /// Get color for different message types
    fn get_color(&self, notification_type: &NotificationType, priority: &Priority) -> u32 {
        match notification_type {
            NotificationType::Trade => match priority {
                Priority::Critical => 0xFF0000, // Red
                Priority::High => 0xFF8C00,     // Dark Orange
                _ => 0x00FF00,                  // Green
            },
            NotificationType::Risk => match priority {
                Priority::Critical => 0xFF0000, // Red
                Priority::High => 0xFF4500,     // Orange Red
                _ => 0xFFD700,                  // Gold
            },
            NotificationType::System => 0x0080FF,    // Blue
            NotificationType::Performance => 0x800080, // Purple
            NotificationType::Error => 0xFF0000,     // Red
        }
    }

    /// Format currency amount with proper precision
    fn format_currency(&self, amount: &str) -> String {
        if let Ok(decimal) = amount.parse::<Decimal>() {
            format!("${:.2}", decimal)
        } else {
            amount.to_string()
        }
    }

    /// Format percentage with proper precision
    fn format_percentage(&self, value: f64) -> String {
        format!("{:.2}%", value)
    }

    /// Get risk level emoji
    fn get_risk_emoji(&self, level: &RiskLevel) -> &'static str {
        match level {
            RiskLevel::Low => "ğŸŸ¢",
            RiskLevel::Medium => "ğŸŸ¡",
            RiskLevel::High => "ğŸŸ ",
            RiskLevel::Critical => "ğŸ”´",
        }
    }

    /// Get trading side emoji
    fn get_side_emoji(&self, side: &str) -> &'static str {
        match side.to_uppercase().as_str() {
            "BUY" | "LONG" => "ğŸ“ˆ",
            "SELL" | "SHORT" => "ğŸ“‰",
            _ => "ğŸ“Š",
        }
    }

    /// Get severity emoji
    fn get_severity_emoji(&self, severity: &ErrorSeverity) -> &'static str {
        match severity {
            ErrorSeverity::Info => "â„¹ï¸",
            ErrorSeverity::Warning => "âš ï¸",
            ErrorSeverity::Error => "âŒ",
            ErrorSeverity::Fatal => "ğŸš¨",
        }
    }
}

impl MessageFormatter for TradingMessageFormatter {
    fn format_trading_event(&self, event: TradingEvent) -> NotificationMessage {
        match event {
            TradingEvent::OrderPlaced { symbol, side, quantity, price, order_id } => {
                let title = format!("{} Order Placed - {}", self.get_side_emoji(&side), symbol);
                let content = format!("Order placed successfully for {} {}", symbol, side.to_uppercase());

                NotificationMessage::new(NotificationType::Trade, title, content)
                    .with_color(self.get_color(&NotificationType::Trade, &Priority::Normal))
                    .add_field("Symbol".to_string(), symbol, true)
                    .add_field("Side".to_string(), side.to_uppercase(), true)
                    .add_field("Quantity".to_string(), quantity, true)
                    .add_field("Price".to_string(), self.format_currency(&price), true)
                    .add_field("Order ID".to_string(), order_id, false)
            },

            TradingEvent::OrderFilled { symbol, side, quantity, price, order_id, commission } => {
                let title = format!("{} Order Filled - {}", self.get_side_emoji(&side), symbol);
                let content = format!("Order filled for {} {} at {}", 
                    symbol, side.to_uppercase(), self.format_currency(&price));

                NotificationMessage::new(NotificationType::Trade, title, content)
                    .with_color(self.get_color(&NotificationType::Trade, &Priority::High))
                    .with_priority(Priority::High)
                    .add_field("Symbol".to_string(), symbol, true)
                    .add_field("Side".to_string(), side.to_uppercase(), true)
                    .add_field("Quantity".to_string(), quantity, true)
                    .add_field("Fill Price".to_string(), self.format_currency(&price), true)
                    .add_field("Commission".to_string(), self.format_currency(&commission), true)
                    .add_field("Order ID".to_string(), order_id, false)
            },

            TradingEvent::OrderCancelled { symbol, side, quantity, price, order_id, reason } => {
                let title = format!("âŒ Order Cancelled - {}", symbol);
                let content = format!("Order cancelled for {} {}: {}", 
                    symbol, side.to_uppercase(), reason);

                NotificationMessage::new(NotificationType::Trade, title, content)
                    .with_color(0xFFD700) // Gold color for cancelled orders
                    .add_field("Symbol".to_string(), symbol, true)
                    .add_field("Side".to_string(), side.to_uppercase(), true)
                    .add_field("Quantity".to_string(), quantity, true)
                    .add_field("Price".to_string(), self.format_currency(&price), true)
                    .add_field("Reason".to_string(), reason, false)
                    .add_field("Order ID".to_string(), order_id, false)
            },

            TradingEvent::PositionOpened { symbol, side, size, entry_price, unrealized_pnl } => {
                let title = format!("{} Position Opened - {}", self.get_side_emoji(&side), symbol);
                let content = format!("New {} position opened for {}", side.to_uppercase(), symbol);

                let pnl_decimal = unrealized_pnl.parse::<Decimal>().unwrap_or_default();
                let color = if pnl_decimal >= Decimal::ZERO { 0x00FF00 } else { 0xFF0000 };

                NotificationMessage::new(NotificationType::Trade, title, content)
                    .with_color(color)
                    .with_priority(Priority::High)
                    .add_field("Symbol".to_string(), symbol, true)
                    .add_field("Side".to_string(), side.to_uppercase(), true)
                    .add_field("Size".to_string(), size, true)
                    .add_field("Entry Price".to_string(), self.format_currency(&entry_price), true)
                    .add_field("Unrealized P&L".to_string(), self.format_currency(&unrealized_pnl), true)
            },

            TradingEvent::PositionClosed { symbol, side, size, exit_price, realized_pnl, holding_time } => {
                let title = format!("{} Position Closed - {}", self.get_side_emoji(&side), symbol);

                let pnl_decimal = realized_pnl.parse::<Decimal>().unwrap_or_default();
                let pnl_emoji = if pnl_decimal > Decimal::ZERO { "ğŸ’°" } 
                               else if pnl_decimal < Decimal::ZERO { "ğŸ’¸" } 
                               else { "â¡ï¸" };

                let content = format!("{} {} position closed with {} P&L", 
                    symbol, side.to_uppercase(), pnl_emoji);

                let color = if pnl_decimal > Decimal::ZERO { 0x00FF00 } else { 0xFF0000 };

                NotificationMessage::new(NotificationType::Trade, title, content)
                    .with_color(color)
                    .with_priority(Priority::High)
                    .add_field("Symbol".to_string(), symbol, true)
                    .add_field("Side".to_string(), side.to_uppercase(), true)
                    .add_field("Size".to_string(), size, true)
                    .add_field("Exit Price".to_string(), self.format_currency(&exit_price), true)
                    .add_field("Realized P&L".to_string(), self.format_currency(&realized_pnl), true)
                    .add_field("Holding Time".to_string(), holding_time, true)
            },

            TradingEvent::RiskAlert { level, message, current_exposure, max_exposure } => {
                let emoji = self.get_risk_emoji(&level);
                let title = format!("{} Risk Alert - {} Level", emoji, level);
                let content = message;

                let priority = match level {
                    RiskLevel::Critical => Priority::Critical,
                    RiskLevel::High => Priority::High,
                    _ => Priority::Normal,
                };

                NotificationMessage::new(NotificationType::Risk, title, content)
                    .with_color(self.get_color(&NotificationType::Risk, &priority))
                    .with_priority(priority)
                    .add_field("Risk Level".to_string(), format!("{} {}", emoji, level), true)
                    .add_field("Current Exposure".to_string(), self.format_currency(&current_exposure), true)
                    .add_field("Max Exposure".to_string(), self.format_currency(&max_exposure), true)
            },

            TradingEvent::SystemError { component, error_message, severity } => {
                let emoji = self.get_severity_emoji(&severity);
                let title = format!("{} System Error - {}", emoji, component);
                let content = error_message;

                let priority = match severity {
                    ErrorSeverity::Fatal => Priority::Critical,
                    ErrorSeverity::Error => Priority::High,
                    _ => Priority::Normal,
                };

                NotificationMessage::new(NotificationType::Error, title, content)
                    .with_color(self.get_color(&NotificationType::Error, &priority))
                    .with_priority(priority)
                    .add_field("Component".to_string(), component, true)
                    .add_field("Severity".to_string(), format!("{} {}", emoji, severity), true)
            },
        }
    }

    fn format_system_message(&self, title: String, content: String, severity: ErrorSeverity) -> NotificationMessage {
        let emoji = self.get_severity_emoji(&severity);
        let formatted_title = format!("{} {}", emoji, title);

        let priority = match severity {
            ErrorSeverity::Fatal => Priority::Critical,
            ErrorSeverity::Error => Priority::High,
            ErrorSeverity::Warning => Priority::Normal,
            ErrorSeverity::Info => Priority::Low,
        };

        NotificationMessage::new(NotificationType::System, formatted_title, content)
            .with_color(self.get_color(&NotificationType::System, &priority))
            .with_priority(priority)
            .add_field("Severity".to_string(), format!("{} {}", emoji, severity), true)
            .add_field("Timestamp".to_string(), Utc::now().format("%Y-%m-%d %H:%M:%S UTC").to_string(), true)
    }

    fn format_performance_report(&self, report: PerformanceReport) -> NotificationMessage {
        let title = format!("ğŸ“Š Performance Report - {}", report.period);
        let content = format!("Trading performance summary for {}", report.period);

        let win_rate_text = self.format_percentage(report.win_rate);
        let pnl_color = if report.total_pnl >= Decimal::ZERO { 0x00FF00 } else { 0xFF0000 };

        let mut message = NotificationMessage::new(NotificationType::Performance, title, content)
            .with_color(pnl_color)
            .add_field("Total Trades".to_string(), report.total_trades.to_string(), true)
            .add_field("Winning Trades".to_string(), report.winning_trades.to_string(), true)
            .add_field("Win Rate".to_string(), win_rate_text, true)
            .add_field("Total P&L".to_string(), self.format_currency(&report.total_pnl.to_string()), true)
            .add_field("Average Win".to_string(), self.format_currency(&report.average_win.to_string()), true)
            .add_field("Average Loss".to_string(), self.format_currency(&report.average_loss.to_string()), true)
            .add_field("Max Drawdown".to_string(), self.format_currency(&report.max_drawdown.to_string()), true)
            .add_field("Portfolio Value".to_string(), self.format_currency(&report.portfolio_value.to_string()), true);

        // Add Sharpe ratio if available
        if let Some(sharpe) = report.sharpe_ratio {
            message = message.add_field("Sharpe Ratio".to_string(), format!("{:.3}", sharpe), true);
        }

        message
    }
}

use crate::discord::{
    types::{DiscordConfig, NotificationMessage, NotificationType},
    notification_queue::{NotificationQueue, NotificationQueueConfig},
    formatter::{MessageFormatter, TradingMessageFormatter},
};
use serenity::{
    async_trait,
    http::Http,
    model::{
        channel::{Message, Embed, EmbedField},
        gateway::Ready,
        id::ChannelId,
    },
    prelude::*,
    utils::Color,
};
use std::sync::Arc;
use tokio::sync::RwLock;
use tracing::{debug, error, info, warn};

/// Discord bot for trading notifications
pub struct DiscordBot {
    http: Arc<Http>,
    config: DiscordConfig,
    notification_queue: Arc<NotificationQueue>,
    formatter: Arc<TradingMessageFormatter>,
    is_connected: Arc<RwLock<bool>>,
}

/// Bot event handler
struct BotHandler;

#[async_trait]
impl EventHandler for BotHandler {
    async fn ready(&self, _: Context, ready: Ready) {
        info!("Discord bot connected as: {}", ready.user.name);
    }

    async fn message(&self, ctx: Context, msg: Message) {
        // Basic command handling can be added here
        if msg.content == "!ping" {
            if let Err(why) = msg.channel_id.say(&ctx.http, "Pong!").await {
                error!("Error sending message: {:?}", why);
            }
        }
    }
}

impl DiscordBot {
    /// Create a new Discord bot instance
    pub fn new(
        config: DiscordConfig,
        queue_config: Option<NotificationQueueConfig>,
    ) -> Result<Self, Box<dyn std::error::Error + Send + Sync>> {
        let http = Arc::new(Http::new(&config.token));
        let queue_config = queue_config.unwrap_or_default();
        let notification_queue = Arc::new(NotificationQueue::new(queue_config));

        let formatter = Arc::new(TradingMessageFormatter::new(
            Some(config.default_channel_id.clone())
        ));

        Ok(Self {
            http,
            config,
            notification_queue,
            formatter,
            is_connected: Arc::new(RwLock::new(false)),
        })
    }

    /// Start the Discord bot
    pub async fn start(&self) -> Result<(), Box<dyn std::error::Error + Send + Sync>> {
        info!("Starting Discord bot...");

        // Create a new client
        let mut client = Client::builder(&self.config.token, GatewayIntents::GUILD_MESSAGES | GatewayIntents::MESSAGE_CONTENT)
            .event_handler(BotHandler)
            .await?;

        // Mark as connected
        {
            let mut connected = self.is_connected.write().await;
            *connected = true;
        }

        // Start the notification queue processing
        let bot_http = Arc::clone(&self.http);
        let bot_config = self.config.clone();
        let bot_formatter = Arc::clone(&self.formatter);

        self.notification_queue.start_processing(move |message| {
            let http = Arc::clone(&bot_http);
            let config = bot_config.clone();
            let formatter = Arc::clone(&bot_formatter);

            async move {
                Self::send_notification_message(http, config, message).await
            }
        }).await?;

        info!("Discord bot started successfully");

        // Start the client (this will block until the bot shuts down)
        if let Err(why) = client.start().await {
            error!("Discord client error: {:?}", why);
            return Err(Box::new(why));
        }

        Ok(())
    }

    /// Stop the Discord bot
    pub async fn stop(&self) -> Result<(), String> {
        info!("Stopping Discord bot...");

        self.notification_queue.stop_processing().await;

        {
            let mut connected = self.is_connected.write().await;
            *connected = false;
        }

        info!("Discord bot stopped");
        Ok(())
    }

    /// Check if the bot is connected
    pub async fn is_connected(&self) -> bool {
        *self.is_connected.read().await
    }

    /// Send a notification message
    pub async fn send_notification(&self, message: NotificationMessage) -> Result<(), String> {
        self.notification_queue.enqueue(message).await
    }

    /// Send a trading event notification
    pub async fn send_trading_event(&self, event: crate::discord::types::TradingEvent) -> Result<(), String> {
        let message = self.formatter.format_trading_event(event);
        self.send_notification(message).await
    }

    /// Send a system message
    pub async fn send_system_message(
        &self,
        title: String,
        content: String,
        severity: crate::discord::types::ErrorSeverity,
    ) -> Result<(), String> {
        let message = self.formatter.format_system_message(title, content, severity);
        self.send_notification(message).await
    }

    /// Send a performance report
    pub async fn send_performance_report(&self, report: crate::discord::formatter::PerformanceReport) -> Result<(), String> {
        let message = self.formatter.format_performance_report(report);
        self.send_notification(message).await
    }

    /// Get notification queue statistics
    pub async fn get_queue_stats(&self) -> crate::discord::notification_queue::QueueStats {
        self.notification_queue.get_stats().await
    }

    /// Send a simple text message to a specific channel
    pub async fn send_simple_message(&self, channel_id: &str, content: &str) -> Result<(), String> {
        let channel_id = channel_id.parse::<u64>()
            .map_err(|e| format!("Invalid channel ID: {}", e))?;
        let channel = ChannelId(channel_id);

        match channel.say(&self.http, content).await {
            Ok(_) => {
                debug!("Simple message sent to channel {}", channel_id);
                Ok(())
            }
            Err(e) => {
                error!("Failed to send simple message: {:?}", e);
                Err(format!("Failed to send message: {}", e))
            }
        }
    }

    /// Internal method to send notification messages
    async fn send_notification_message(
        http: Arc<Http>,
        config: DiscordConfig,
        message: NotificationMessage,
    ) -> Result<(), String> {
        // Determine target channel
        let target_channel = message.channel_id
            .or_else(|| Self::get_channel_for_type(&config, &message.notification_type))
            .unwrap_or_else(|| config.default_channel_id.clone());

        let channel_id = target_channel.parse::<u64>()
            .map_err(|e| format!("Invalid channel ID: {}", e))?;
        let channel = ChannelId(channel_id);

        // Send message based on configuration
        if config.enable_embeds {
            Self::send_embed_message(&http, channel, &message).await
        } else {
            Self::send_text_message(&http, channel, &message).await
        }
    }

    /// Get appropriate channel for notification type
    fn get_channel_for_type(config: &DiscordConfig, notification_type: &NotificationType) -> Option<String> {
        match notification_type {
            NotificationType::Trade => config.trading_channel_id.clone(),
            NotificationType::Risk => config.risk_channel_id.clone(),
            NotificationType::System | NotificationType::Error => config.system_channel_id.clone(),
            _ => None,
        }
    }

    /// Send an embed message
    async fn send_embed_message(
        http: &Arc<Http>,
        channel: ChannelId,
        message: &NotificationMessage,
    ) -> Result<(), String> {
        let color = message.color.unwrap_or(0x0080FF);
        let mut embed = Embed::fake(|e| {
            e.title(&message.title)
             .description(&message.content)
             .color(Color::new(color))
             .timestamp(&message.timestamp)
        });

        // Add fields
        let mut fields = Vec::new();
        for field in &message.fields {
            fields.push(EmbedField {
                name: field.name.clone(),
                value: field.value.clone(),
                inline: field.inline,
            });
        }
        embed.fields = fields;

        match channel.send_message(http, |m| m.set_embed(embed)).await {
            Ok(_) => {
                debug!("Embed message sent successfully: {}", message.id);
                Ok(())
            }
            Err(e) => {
                error!("Failed to send embed message {}: {:?}", message.id, e);
                Err(format!("Failed to send embed: {}", e))
            }
        }
    }

    /// Send a text message
    async fn send_text_message(
        http: &Arc<Http>,
        channel: ChannelId,
        message: &NotificationMessage,
    ) -> Result<(), String> {
        let mut content = format!("**{}**\n{}", message.title, message.content);

        // Add fields as text
        for field in &message.fields {
            if field.inline {
                content.push_str(&format!("\nâ€¢ **{}**: {}", field.name, field.value));
            } else {
                content.push_str(&format!("\n\n**{}**\n{}", field.name, field.value));
            }
        }

        // Add timestamp
        content.push_str(&format!("\n*{}*", message.timestamp.format("%Y-%m-%d %H:%M:%S UTC")));

        match channel.say(http, &content).await {
            Ok(_) => {
                debug!("Text message sent successfully: {}", message.id);
                Ok(())
            }
            Err(e) => {
                error!("Failed to send text message {}: {:?}", message.id, e);
                Err(format!("Failed to send text: {}", e))
            }
        }
    }

    /// Test the bot connection
    pub async fn test_connection(&self) -> Result<(), String> {
        let test_message = "ğŸ¤– Discord bot connection test successful!";
        self.send_simple_message(&self.config.default_channel_id, test_message).await
    }

    /// Get bot information
    pub async fn get_bot_info(&self) -> Result<String, String> {
        match self.http.get_current_user().await {
            Ok(user) => Ok(format!(
                "Bot: {} ({})\nConnected: {}", 
                user.name, 
                user.id,
                self.is_connected().await
            )),
            Err(e) => Err(format!("Failed to get bot info: {}", e))
        }
    }
}

