        let messages_sent_clone = messages_sent.clone();

        // Start processing with a mock sender
        let result = queue.start_processing(move |_message| {
            let counter = messages_sent_clone.clone();
            async move {
                counter.fetch_add(1, std::sync::atomic::Ordering::SeqCst);
                Ok(())
            }
        }).await;
        assert!(result.is_ok());

        // Enqueue some test messages
        for i in 0..3 {
            let message = NotificationMessage::new(
                NotificationType::Trade,
                format!("Test Message {}", i),
                "Test content".to_string(),
            );
            queue.enqueue(message).await.unwrap();
        }

        // Wait for processing
        sleep(Duration::from_millis(300)).await;

        // Check that messages were processed
        let sent_count = messages_sent.load(std::sync::atomic::Ordering::SeqCst);
        assert_eq!(sent_count, 3);

        queue.stop_processing().await;
    }

    #[test]
    fn test_trading_message_formatter() {
        let formatter = TradingMessageFormatter::new(None);

        let event = TradingEvent::OrderFilled {
            symbol: "BTCUSDT".to_string(),
            side: "buy".to_string(),
            quantity: "0.1".to_string(),
            price: "45000.00".to_string(),
            order_id: "12345".to_string(),
            commission: "1.35".to_string(),
        };

        let message = formatter.format_trading_event(event);

        assert_eq!(message.notification_type, NotificationType::Trade);
        assert!(message.title.contains("BTCUSDT"));
        assert!(message.content.contains("BUY"));
        assert_eq!(message.fields.len(), 6); // Symbol, Side, Quantity, Fill Price, Commission, Order ID
    }

    #[test]
    fn test_system_message_formatter() {
        let formatter = TradingMessageFormatter::new(None);

        let message = formatter.format_system_message(
            "System Started".to_string(),
            "Trading system has started successfully".to_string(),
            ErrorSeverity::Info,
        );

        assert_eq!(message.notification_type, NotificationType::System);
        assert!(message.title.contains("System Started"));
        assert_eq!(message.fields.len(), 2); // Severity and Timestamp
    }

    #[test]
    fn test_performance_report_formatter() {
        let formatter = TradingMessageFormatter::new(None);

        let report = PerformanceReport {
            period: "Daily".to_string(),
            total_trades: 15,
            winning_trades: 10,
            losing_trades: 5,
            total_pnl: Decimal::new(12505, 2), // 125.05
            win_rate: 66.67,
            average_win: Decimal::new(2500, 2), // 25.00
            average_loss: Decimal::new(-850, 2), // -8.50
            max_drawdown: Decimal::new(-350, 2), // -3.50
            sharpe_ratio: Some(1.85),
            portfolio_value: Decimal::new(1012505, 2), // 10125.05
        };

        let message = formatter.format_performance_report(report);

        assert_eq!(message.notification_type, NotificationType::Performance);
        assert!(message.title.contains("Performance Report"));
        assert!(message.fields.len() >= 8); // Should have at least basic fields

        // Check that Sharpe ratio field is included
        let has_sharpe = message.fields.iter().any(|f| f.name == "Sharpe Ratio");
        assert!(has_sharpe);
    }

    #[test]
    fn test_notification_message_builder() {
        let message = NotificationMessage::new(
            NotificationType::Risk,
            "Risk Alert".to_string(),
            "High risk detected".to_string(),
        )
        .with_color(0xFF0000)
        .with_priority(crate::discord::types::Priority::High)
        .add_field("Risk Level".to_string(), "High".to_string(), true)
        .add_field("Details".to_string(), "Exposure too high".to_string(), false);

        assert_eq!(message.notification_type, NotificationType::Risk);
        assert_eq!(message.color, Some(0xFF0000));
        assert_eq!(message.priority, crate::discord::types::Priority::High);
        assert_eq!(message.fields.len(), 2);
        assert!(message.fields[0].inline);
        assert!(!message.fields[1].inline);
    }

    #[test]
    fn test_discord_config_default() {
        let config = DiscordConfig::default();

        assert!(config.token.is_empty());
        assert!(config.default_channel_id.is_empty());
        assert!(config.enable_embeds);
        assert_eq!(config.max_retries, 3);
        assert_eq!(config.retry_delay_seconds, 5);
    }

    #[tokio::test]
    async fn test_queue_rate_limiting() {
        let config = NotificationQueueConfig {
            rate_limit_per_minute: 2, // Very low limit for testing
            processing_interval_ms: 50,
            ..Default::default()
        };
        let queue = NotificationQueue::new(config);

        let messages_sent = std::sync::Arc::new(std::sync::atomic::AtomicU32::new(0));
        let messages_sent_clone = messages_sent.clone();

        // Start processing
        let result = queue.start_processing(move |_message| {
            let counter = messages_sent_clone.clone();
            async move {
                counter.fetch_add(1, std::sync::atomic::Ordering::SeqCst);
                Ok(())
            }
        }).await;
        assert!(result.is_ok());

        // Enqueue more messages than rate limit allows
        for i in 0..5 {
            let message = NotificationMessage::new(
                NotificationType::Trade,
                format!("Test Message {}", i),
                "Test content".to_string(),
            );
            queue.enqueue(message).await.unwrap();
        }

        // Wait for processing
        sleep(Duration::from_millis(200)).await;

        // Should be rate limited to 2 messages
        let sent_count = messages_sent.load(std::sync::atomic::Ordering::SeqCst);
        assert!(sent_count <= 2);

        let stats = queue.get_stats().await;
        assert!(stats.rate_limited_count > 0);

        queue.stop_processing().await;
    }

    #[tokio::test]
    async fn test_queue_full_behavior() {
        let config = NotificationQueueConfig {
            max_queue_size: 3,
            ..Default::default()
        };
        let queue = NotificationQueue::new(config);

        // Fill the queue to capacity
        for i in 0..3 {
            let message = NotificationMessage::new(
                NotificationType::Trade,
                format!("Test Message {}", i),
                "Test content".to_string(),
            );
            let result = queue.enqueue(message).await;
            assert!(result.is_ok());
        }

        // Try to add one more message (should trigger dropping)
        let message = NotificationMessage::new(
            NotificationType::Trade,
            "Overflow Message".to_string(),
            "This should trigger dropping".to_string(),
        )
        .with_priority(crate::discord::types::Priority::High);

        let result = queue.enqueue(message).await;
        assert!(result.is_ok()); // Should succeed by dropping a lower priority message

        let stats = queue.get_stats().await;
        assert_eq!(stats.current_queue_size, 3);
        assert_eq!(stats.messages_dropped, 1);
    }
}

