        // æ‡‰è©²åªä¿ç•™æœ€æ–°çš„ 5 ç­†è¨˜éŒ„
        assert_eq!(history.len(), 5);

        // æ¸¬è©¦é™åˆ¶æ•¸é‡ç²å–
        let limited_history = cache.get_history("ETHUSDT", Some(3)).await;
