use crate::{ExchangeError, MarketData};
use std::collections::HashMap;
use std::sync::Arc;
use tokio::sync::{broadcast, RwLock};

/// æ•¸æ“šç·©å­˜å’Œåˆ†ç™¼æ©Ÿåˆ¶
#[derive(Debug, Clone)]
pub struct MarketDataCache {
    /// æœ€æ–°å¸‚å ´æ•¸æ“šç·©å­˜
    data_cache: Arc<RwLock<HashMap<String, MarketData>>>,
    /// å»£æ’­ç™¼é€å™¨ç”¨æ–¼æ•¸æ“šåˆ†ç™¼
    broadcaster: broadcast::Sender<MarketData>,
    /// æ•¸æ“šæ­·å²è¨˜éŒ„ï¼ˆé™åˆ¶å¤§å°é¿å…è¨˜æ†¶é«”æ´©æ¼ï¼‰
    history: Arc<RwLock<HashMap<String, Vec<MarketData>>>>,
    /// æ¯å€‹ symbol çš„æ­·å²æ•¸æ“šæœ€å¤§è¨˜éŒ„æ•¸
    max_history_size: usize,
}

impl MarketDataCache {
    /// å‰µå»ºæ–°çš„å¸‚å ´æ•¸æ“šç·©å­˜
    pub fn new(max_history_size: usize) -> Self {
        let (broadcaster, _) = broadcast::channel(1000); // ç·©è¡å€å¤§å°ç‚º 1000

        Self {
            data_cache: Arc::new(RwLock::new(HashMap::new())),
            broadcaster,
            history: Arc::new(RwLock::new(HashMap::new())),
            max_history_size,
        }
    }

    /// æ›´æ–°å¸‚å ´æ•¸æ“šä¸¦åˆ†ç™¼çµ¦è¨‚é–±è€…
    pub async fn update_market_data(&self, data: MarketData) -> Result<(), ExchangeError> {
        let symbol = data.symbol.clone();

        // æ›´æ–°å¿«å–
        {
            let mut cache = self.data_cache.write().await;
            cache.insert(symbol.clone(), data.clone());
        }

        // æ›´æ–°æ­·å²è¨˜éŒ„
        {
            let mut history = self.history.write().await;
            let symbol_history = history.entry(symbol).or_insert_with(Vec::new);

            symbol_history.push(data.clone());

            // é™åˆ¶æ­·å²è¨˜éŒ„å¤§å°
            if symbol_history.len() > self.max_history_size {
                symbol_history.remove(0);
            }
        }

        // å»£æ’­æ•¸æ“šçµ¦æ‰€æœ‰è¨‚é–±è€…
        if self.broadcaster.send(data).is_err() {
            // å¦‚æœæ²’æœ‰è¨‚é–±è€…ï¼Œé€™æ˜¯æ­£å¸¸çš„ï¼Œä¸éœ€è¦å ±éŒ¯
        }

        Ok(())
    }

    /// ç²å–æœ€æ–°å¸‚å ´æ•¸æ“š
    pub async fn get_latest_data(&self, symbol: &str) -> Option<MarketData> {
        let cache = self.data_cache.read().await;
        cache.get(symbol).cloned()
    }

    /// ç²å–æ‰€æœ‰æœ€æ–°å¸‚å ´æ•¸æ“š
    pub async fn get_all_latest_data(&self) -> HashMap<String, MarketData> {
        let cache = self.data_cache.read().await;
        cache.clone()
    }

    /// ç²å–æ­·å²æ•¸æ“š
    pub async fn get_history(&self, symbol: &str, limit: Option<usize>) -> Vec<MarketData> {
        let history = self.history.read().await;
        if let Some(symbol_history) = history.get(symbol) {
            match limit {
                Some(n) => {
                    let start = symbol_history.len().saturating_sub(n);
                    symbol_history[start..].to_vec()
                }
                None => symbol_history.clone(),
            }
        } else {
            Vec::new()
        }
    }

    /// è¨‚é–±å¸‚å ´æ•¸æ“šæ›´æ–°
    pub fn subscribe(&self) -> broadcast::Receiver<MarketData> {
        self.broadcaster.subscribe()
    }

    /// æ¸…é™¤ç‰¹å®š symbol çš„æ•¸æ“š
    pub async fn clear_symbol(&self, symbol: &str) {
        {
            let mut cache = self.data_cache.write().await;
            cache.remove(symbol);
        }

        {
            let mut history = self.history.write().await;
            history.remove(symbol);
        }
    }

    /// æ¸…é™¤æ‰€æœ‰æ•¸æ“š
    pub async fn clear_all(&self) {
        {
            let mut cache = self.data_cache.write().await;
            cache.clear();
        }

        {
            let mut history = self.history.write().await;
            history.clear();
        }
    }

    /// ç²å–å¿«å–çµ±è¨ˆè³‡è¨Š
    pub async fn get_cache_stats(&self) -> CacheStats {
        let cache = self.data_cache.read().await;
        let history = self.history.read().await;

        let total_cached_symbols = cache.len();
        let total_history_entries: usize = history.values().map(|v| v.len()).sum();

        CacheStats {
            total_cached_symbols,
            total_history_entries,
            max_history_size: self.max_history_size,
        }
    }
}

/// ç·©å­˜çµ±è¨ˆè³‡è¨Š
#[derive(Debug)]
pub struct CacheStats {
    pub total_cached_symbols: usize,
    pub total_history_entries: usize,
    pub max_history_size: usize,
}

