use futures_util::StreamExt;

/// æ•¸æ“šåŒæ­¥é…ç½®
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct DataSyncConfig {
    /// åŒæ­¥é–“éš” (ç§’)
    pub sync_interval_seconds: u64,
    /// æ”¯æ´çš„äº¤æ˜“æ‰€
    pub enabled_exchanges: Vec<String>,
    /// éœ€è¦åŒæ­¥çš„äº¤æ˜“å°
    pub symbols: Vec<String>,
    /// æ•¸æ“šæ ¼å¼é©—è­‰æ˜¯å¦åš´æ ¼
    pub strict_validation: bool,
    /// æœ€å¤§é‡è©¦æ¬¡æ•¸
    pub max_retries: u32,
    /// é€£æ¥è¶…æ™‚ (æ¯«ç§’)
    pub connection_timeout_ms: u64,
    /// æ•¸æ“šå»¶é²å®¹å¿åº¦ (æ¯«ç§’)
    pub latency_tolerance_ms: u64,
}

impl Default for DataSyncConfig {
    fn default() -> Self {
        Self {
            sync_interval_seconds: 1,
            enabled_exchanges: vec![
                "binance".to_string(),
                "okx".to_string(),
                "bybit".to_string(),
            ],
            symbols: vec!["BTCUSDT".to_string(), "ETHUSDT".to_string()],
            strict_validation: true,
            max_retries: 3,
            connection_timeout_ms: 5000,
            latency_tolerance_ms: 1000,
        }
    }
}

/// æ•¸æ“šæºç‹€æ…‹
#[derive(Debug, Clone)]
pub struct DataSourceStatus {
    pub exchange_name: String,
    pub is_connected: bool,
    pub last_data_time: Option<Instant>,
    pub error_count: u32,
    pub total_messages: u64,
    pub failed_messages: u64,
    pub connection_uptime: Duration,
}

/// æ•¸æ“šåŒæ­¥çµ±è¨ˆ
#[derive(Debug, Clone)]
pub struct SyncStats {
    pub active_exchanges: usize,
    pub total_symbols: usize,
    pub messages_per_second: f64,
    pub sync_conflicts: u64,
    pub data_validation_errors: u64,
    pub network_errors: u64,
}

/// å¤šäº¤æ˜“æ‰€æ•¸æ“šåŒæ­¥å”èª¿å™¨
/// è² è²¬å”èª¿ä¾†è‡ªå¤šå€‹äº¤æ˜“æ‰€çš„æ•¸æ“šæµï¼Œé€²è¡Œæ ¼å¼æ¨™æº–åŒ–ã€é©—è­‰å’ŒåŒæ­¥å­˜å„²
pub struct DataSyncCoordinator {
    /// æ•¸æ“šåŒæ­¥é…ç½®
    config: DataSyncConfig,
    /// äº¤æ˜“æ‰€é©é…å™¨æ˜ å°„
    exchanges: Arc<RwLock<HashMap<String, Arc<dyn ExchangeAdapter>>>>,
    /// æ•¸æ“šå­˜å„²æœå‹™
    storage_service: Arc<MarketDataStorageService>,
    /// æ•¸æ“šæºç‹€æ…‹è¿½è¹¤
    source_status: Arc<RwLock<HashMap<String, DataSourceStatus>>>,
    /// åŒæ­¥çµ±è¨ˆ
    sync_stats: Arc<RwLock<SyncStats>>,
    /// å”èª¿å™¨é‹è¡Œç‹€æ…‹
    is_running: Arc<RwLock<bool>>,
    /// æ•¸æ“šè³ªé‡ç›£æ§å™¨
    data_validator: DataValidator,
    /// éŒ¯èª¤å»£æ’­å™¨
    error_broadcaster: broadcast::Sender<SyncError>,
}

impl DataSyncCoordinator {
    /// å‰µå»ºæ–°çš„æ•¸æ“šåŒæ­¥å”èª¿å™¨
    pub async fn new(
        config: DataSyncConfig,
        storage_config: DataStorageConfig,
    ) -> Result<Self, ExchangeError> {
        let storage_service = Arc::new(
            MarketDataStorageService::new(storage_config)
                .await
                .map_err(|e| ExchangeError::ServiceError(format!("Failed to create storage service: {}", e)))?
        );

        let (error_broadcaster, _) = broadcast::channel(1000);

        let coordinator = Self {
            config: config.clone(),
            exchanges: Arc::new(RwLock::new(HashMap::new())),
            storage_service,
            source_status: Arc::new(RwLock::new(HashMap::new())),
            sync_stats: Arc::new(RwLock::new(SyncStats {
                active_exchanges: 0,
                total_symbols: config.symbols.len(),
                messages_per_second: 0.0,
                sync_conflicts: 0,
                data_validation_errors: 0,
                network_errors: 0,
            })),
            is_running: Arc::new(RwLock::new(false)),
            data_validator: DataValidator::new(config.strict_validation),
            error_broadcaster,
        };

        info!("Data sync coordinator initialized with {} exchanges and {} symbols", 
              config.enabled_exchanges.len(), config.symbols.len());

        Ok(coordinator)
    }

    /// è¨»å†Šäº¤æ˜“æ‰€é©é…å™¨
    pub async fn register_exchange(&self, adapter: Arc<dyn ExchangeAdapter>) -> Result<(), ExchangeError> {
        let exchange_name = adapter.name().to_string();

        // æª¢æŸ¥äº¤æ˜“æ‰€æ˜¯å¦åœ¨é…ç½®çš„å•Ÿç”¨åˆ—è¡¨ä¸­
        if !self.config.enabled_exchanges.contains(&exchange_name) {
            return Err(ExchangeError::ConfigError(
                format!("Exchange {} is not enabled in configuration", exchange_name)
            ));
        }

        // æ¸¬è©¦é€£æ¥
        if !adapter.test_connection().await.unwrap_or(false) {
            warn!("Failed to connect to exchange: {}", exchange_name);
            return Err(ExchangeError::NetworkError(
                format!("Unable to connect to {}", exchange_name)
            ));
        }

        // è¨»å†Šé©é…å™¨
        let mut exchanges = self.exchanges.write().await;
        exchanges.insert(exchange_name.clone(), adapter);

        // åˆå§‹åŒ–ç‹€æ…‹è¿½è¹¤
        let mut status = self.source_status.write().await;
        status.insert(exchange_name.clone(), DataSourceStatus {
            exchange_name: exchange_name.clone(),
            is_connected: true,
            last_data_time: None,
            error_count: 0,
            total_messages: 0,
            failed_messages: 0,
            connection_uptime: Duration::ZERO,
        });

        info!("Registered exchange adapter: {}", exchange_name);
        Ok(())
    }

    /// å•Ÿå‹•æ•¸æ“šåŒæ­¥
    pub async fn start(&self) -> Result<(), ExchangeError> {
        let mut is_running = self.is_running.write().await;
        if *is_running {
            return Err(ExchangeError::ServiceError("Data sync coordinator already running".to_string()));
        }
        *is_running = true;

        info!("Starting data sync coordinator");

        // å•Ÿå‹•å­˜å„²æœå‹™
        self.storage_service.start().await?;

        // ç‚ºæ¯å€‹è¨»å†Šçš„äº¤æ˜“æ‰€å•Ÿå‹•æ•¸æ“šæµ
        let exchanges = self.exchanges.read().await;
        for (exchange_name, adapter) in exchanges.iter() {
            self.start_exchange_data_stream(exchange_name.clone(), Arc::clone(adapter)).await?;
        }

        // å•Ÿå‹•ç›£æ§å’Œçµ±è¨ˆä»»å‹™
        self.start_monitoring_task().await;

        info!("Data sync coordinator started successfully");
        Ok(())
    }

    /// åœæ­¢æ•¸æ“šåŒæ­¥
    pub async fn stop(&self) -> Result<(), ExchangeError> {
        let mut is_running = self.is_running.write().await;
        if !*is_running {
            return Ok(());
        }
        *is_running = false;

        info!("Stopping data sync coordinator");

        // åœæ­¢å­˜å„²æœå‹™
        self.storage_service.stop().await?;

        info!("Data sync coordinator stopped");
        Ok(())
    }

    /// ç²å–åŒæ­¥ç‹€æ…‹
    pub async fn get_sync_status(&self) -> SyncStatus {
        let exchanges = self.exchanges.read().await;
        let source_status = self.source_status.read().await;
        let sync_stats = self.sync_stats.read().await;

        let exchange_statuses: HashMap<String, DataSourceStatus> = 
            source_status.iter().map(|(k, v)| (k.clone(), v.clone())).collect();

        SyncStatus {
            is_running: *self.is_running.read().await,
            total_exchanges: exchanges.len(),
            active_exchanges: exchange_statuses.values().filter(|s| s.is_connected).count(),
            exchange_statuses,
            sync_stats: sync_stats.clone(),
        }
    }

    /// è¨‚é–±åŒæ­¥éŒ¯èª¤
    pub fn subscribe_errors(&self) -> broadcast::Receiver<SyncError> {
        self.error_broadcaster.subscribe()
    }

    /// ç‚ºç‰¹å®šäº¤æ˜“æ‰€å•Ÿå‹•æ•¸æ“šæµ
    async fn start_exchange_data_stream(
        &self,
        exchange_name: String,
        adapter: Arc<dyn ExchangeAdapter>
    ) -> Result<(), ExchangeError> {
        let symbols = self.config.symbols.clone();
        let storage_service = Arc::clone(&self.storage_service);
        let source_status = Arc::clone(&self.source_status);
        let is_running = Arc::clone(&self.is_running);
        let validator = self.data_validator.clone();
        let error_broadcaster = self.error_broadcaster.clone();

        // è¨‚é–±å¸‚å ´æ•¸æ“š
        let mut data_stream = adapter.subscribe_market_data(symbols).await?;

        tokio::spawn(async move {
            let mut retry_count = 0;
            let start_time = Instant::now();

            while *is_running.read().await && retry_count < 5 {
                match data_stream.next().await {
                    Some(Ok(market_data)) => {
                        retry_count = 0; // é‡ç½®é‡è©¦è¨ˆæ•¸

                        // æ•¸æ“šé©—è­‰
                        if let Err(validation_error) = validator.validate(&market_data) {
                            warn!("Data validation failed for {}: {}", exchange_name, validation_error);

                            // æ›´æ–°ç‹€æ…‹
                            if let Ok(mut status) = source_status.write() {
                                if let Some(exchange_status) = status.get_mut(&exchange_name) {
                                    exchange_status.failed_messages += 1;
                                }
                            }

                            // å»£æ’­éŒ¯èª¤
                            let _ = error_broadcaster.send(SyncError::ValidationError {
                                exchange: exchange_name.clone(),
                                symbol: market_data.symbol.clone(),
                                error: validation_error,
                            });

                            continue;
                        }

                        // å­˜å„²æ•¸æ“š
                        if let Err(storage_error) = storage_service.store_market_data(market_data.clone(), exchange_name.clone()).await {
                            error!("Failed to store market data from {}: {}", exchange_name, storage_error);

                            // å»£æ’­éŒ¯èª¤
                            let _ = error_broadcaster.send(SyncError::StorageError {
                                exchange: exchange_name.clone(),
                                symbol: market_data.symbol.clone(),
                                error: storage_error.to_string(),
                            });
                        } else {
                            debug!("Successfully stored data from {}: {}", exchange_name, market_data.symbol);
                        }

                        // æ›´æ–°ç‹€æ…‹
                        if let Ok(mut status) = source_status.write() {
                            if let Some(exchange_status) = status.get_mut(&exchange_name) {
                                exchange_status.last_data_time = Some(Instant::now());
                                exchange_status.total_messages += 1;
                                exchange_status.connection_uptime = start_time.elapsed();
                                exchange_status.is_connected = true;
                            }
                        }
                    },
                    Some(Err(e)) => {
                        retry_count += 1;
                        warn!("Error receiving data from {}: {} (retry: {})", exchange_name, e, retry_count);

                        // æ›´æ–°éŒ¯èª¤ç‹€æ…‹
                        if let Ok(mut status) = source_status.write() {
                            if let Some(exchange_status) = status.get_mut(&exchange_name) {
                                exchange_status.error_count += 1;
                                exchange_status.is_connected = false;
                            }
                        }

                        // å»£æ’­ç¶²è·¯éŒ¯èª¤
                        let _ = error_broadcaster.send(SyncError::NetworkError {
                            exchange: exchange_name.clone(),
                            error: e.to_string(),
                        });

                        // ç­‰å¾…å¾Œé‡è©¦
                        tokio::time::sleep(Duration::from_secs(2_u64.pow(retry_count.min(5)))).await;
                    },
                    None => {
                        warn!("Data stream ended for exchange: {}", exchange_name);
                        break;
                    }
                }
            }

            error!("Data stream task ended for exchange: {}", exchange_name);
        });

        Ok(())
    }

    /// å•Ÿå‹•ç›£æ§ä»»å‹™
    async fn start_monitoring_task(&self) {
        let source_status = Arc::clone(&self.source_status);
        let sync_stats = Arc::clone(&self.sync_stats);
        let is_running = Arc::clone(&self.is_running);
        let error_broadcaster = self.error_broadcaster.clone();

        tokio::spawn(async move {
            let mut interval_timer = interval(Duration::from_secs(10)); // æ¯10ç§’çµ±è¨ˆä¸€æ¬¡

            let mut last_total_messages = 0u64;

            while *is_running.read().await {
                interval_timer.tick().await;

                // è¨ˆç®—çµ±è¨ˆä¿¡æ¯
                if let (Ok(status), Ok(mut stats)) = (source_status.read(), sync_stats.write()) {
                    let current_total: u64 = status.values().map(|s| s.total_messages).sum();
                    let messages_since_last = current_total.saturating_sub(last_total_messages);

                    stats.active_exchanges = status.values().filter(|s| s.is_connected).count();
                    stats.messages_per_second = messages_since_last as f64 / 10.0;
                    stats.network_errors = status.values().map(|s| s.error_count as u64).sum();

                    last_total_messages = current_total;

                    debug!("Sync stats: active={}, msg/s={:.2}, errors={}", 
                           stats.active_exchanges, stats.messages_per_second, stats.network_errors);

                    // æª¢æŸ¥å¥åº·ç‹€æ…‹
                    for (exchange_name, exchange_status) in status.iter() {
                        if !exchange_status.is_connected {
                            let _ = error_broadcaster.send(SyncError::ConnectionLost {
                                exchange: exchange_name.clone(),
                            });
                        }
                    }
                }
            }

            info!("Monitoring task stopped");
        });
    }
}

/// æ•¸æ“šé©—è­‰å™¨
#[derive(Debug, Clone)]
pub struct DataValidator {
    strict_mode: bool,
}

impl DataValidator {
    pub fn new(strict_mode: bool) -> Self {
        Self { strict_mode }
    }

    pub fn validate(&self, data: &MarketData) -> Result<(), String> {
        // åŸºæœ¬å­—æ®µé©—è­‰
        if data.symbol.is_empty() {
            return Err("Symbol is empty".to_string());
        }

        if data.price.is_zero() || data.price.is_sign_negative() {
            return Err("Price must be positive".to_string());
        }

        if self.strict_mode {
            // åš´æ ¼æ¨¡å¼ä¸‹çš„é¡å¤–é©—è­‰
            if data.bid >= data.ask {
                return Err("Bid price must be less than ask price".to_string());
            }

            if data.volume.is_sign_negative() {
                return Err("Volume cannot be negative".to_string());
            }

            if data.bid_size.is_sign_negative() || data.ask_size.is_sign_negative() {
                return Err("Order sizes cannot be negative".to_string());
            }
        }

        Ok(())
    }
}

