}

impl DataNormalizer {
    /// å‰µå»ºæ–°çš„æ•¸æ“šæ¨™æº–åŒ–å™¨
    pub fn new() -> Self {
        let mut normalizer = Self {
            symbol_mappings: HashMap::new(),
            exchange_rules: HashMap::new(),
            validation_rules: ValidationRules::default(),
        };

        // åˆå§‹åŒ–é è¨­è¦å‰‡
        normalizer.setup_default_rules();
        normalizer
    }

    /// æ¨™æº–åŒ–å¸‚å ´æ•¸æ“š
    pub fn normalize_market_data(
        &self,
        raw_data: &MarketData,
        exchange: &str,
    ) -> Result<StandardizedMarketData, ExchangeError> {
        // ç²å–äº¤æ˜“æ‰€è¦å‰‡
        let rules = self.exchange_rules.get(exchange)
            .ok_or_else(|| ExchangeError::ConfigError(format!("No rules found for exchange: {}", exchange)))?;

        // ç¬¦è™Ÿæ¨™æº–åŒ–
        let standardized_symbol = self.normalize_symbol(&raw_data.symbol, exchange)?;

        // ç²¾åº¦èª¿æ•´
        let normalized_price = self.adjust_precision(raw_data.price, rules.price_precision);
        let normalized_volume = self.adjust_precision(raw_data.volume, rules.quantity_precision);
        let normalized_bid = self.adjust_precision(raw_data.bid, rules.price_precision);
        let normalized_ask = self.adjust_precision(raw_data.ask, rules.price_precision);
        let normalized_bid_size = self.adjust_precision(raw_data.bid_size, rules.quantity_precision);
        let normalized_ask_size = self.adjust_precision(raw_data.ask_size, rules.quantity_precision);

        // æ™‚é–“æˆ³æ¨™æº–åŒ–
        let normalized_timestamp = self.normalize_timestamp(raw_data.timestamp, &rules.timestamp_unit);

        // è¨ˆç®—æ•¸æ“šè³ªé‡å¾—åˆ†
        let quality_score = self.calculate_quality_score(raw_data, rules);

        let standardized_data = StandardizedMarketData {
            symbol: standardized_symbol,
            raw_symbol: raw_data.symbol.clone(),
            exchange: exchange.to_string(),
            timestamp: normalized_timestamp,
            price: normalized_price,
            volume: normalized_volume,
            bid: normalized_bid,
            ask: normalized_ask,
            bid_size: normalized_bid_size,
            ask_size: normalized_ask_size,
            high_24h: None, // éœ€è¦é¡å¤–æ•¸æ“šæº
            low_24h: None,  // éœ€è¦é¡å¤–æ•¸æ“šæº
            change_24h: None, // éœ€è¦é¡å¤–æ•¸æ“šæº
            quality_score,
        };

        // é©—è­‰æ¨™æº–åŒ–å¾Œçš„æ•¸æ“š
        self.validate_standardized_data(&standardized_data)?;

        Ok(standardized_data)
    }

    /// æ‰¹é‡æ¨™æº–åŒ–æ•¸æ“š
    pub fn normalize_market_data_batch(
        &self,
        raw_data_batch: &[(MarketData, String)],
    ) -> Vec<Result<StandardizedMarketData, ExchangeError>> {
        raw_data_batch.iter()
            .map(|(data, exchange)| self.normalize_market_data(data, exchange))
            .collect()
    }

    /// ç¬¦è™Ÿæ¨™æº–åŒ–
    fn normalize_symbol(&self, raw_symbol: &str, exchange: &str) -> Result<String, ExchangeError> {
        if let Some(exchange_mappings) = self.symbol_mappings.get(exchange) {
            if let Some(standard_symbol) = exchange_mappings.get(raw_symbol) {
                return Ok(standard_symbol.clone());
            }
        }

        // å¦‚æœæ²’æœ‰æ‰¾åˆ°æ˜ å°„ï¼Œå˜—è©¦é€šç”¨è½‰æ›
        self.apply_generic_symbol_conversion(raw_symbol)
    }

    /// é€šç”¨ç¬¦è™Ÿè½‰æ›
    fn apply_generic_symbol_conversion(&self, raw_symbol: &str) -> Result<String, ExchangeError> {
        // ç§»é™¤å¸¸è¦‹çš„å¾Œç¶´
        let cleaned = raw_symbol
            .replace("-PERP", "")
            .replace("PERP", "")
            .replace("-SWAP", "")
            .replace("SWAP", "");

        // è½‰æ›ç‚ºæ¨™æº–æ ¼å¼ BTC/USDT
        if cleaned.contains("USDT") && !cleaned.contains('/') {
            let base = cleaned.replace("USDT", "");
            return Ok(format!("{}/USDT", base));
        }

        if cleaned.contains("BTC") && !cleaned.contains('/') && cleaned != "BTC" {
            let base = cleaned.replace("BTC", "");
            return Ok(format!("{}/BTC", base));
        }

        // å¦‚æœç„¡æ³•è½‰æ›ï¼Œè¿”å›åŸå§‹ç¬¦è™Ÿ
        warn!("Unable to normalize symbol: {}", raw_symbol);
        Ok(raw_symbol.to_string())
    }

    /// ç²¾åº¦èª¿æ•´
    fn adjust_precision(&self, value: Decimal, precision: Option<u32>) -> Decimal {
        if let Some(prec) = precision {
            value.round_dp(prec)
        } else {
            value
        }
    }

    /// æ™‚é–“æˆ³æ¨™æº–åŒ–
    fn normalize_timestamp(&self, timestamp: DateTime<Utc>, unit: &TimestampUnit) -> DateTime<Utc> {
        // ç›®å‰å‡è¨­è¼¸å…¥å·²ç¶“æ˜¯æ­£ç¢ºçš„ DateTime<Utc>
        // åœ¨å¯¦éš›å¯¦ä½œä¸­ï¼Œå¯èƒ½éœ€è¦å¾æ¯«ç§’ã€ç§’ç­‰è½‰æ›
        timestamp
    }

    /// è¨ˆç®—æ•¸æ“šè³ªé‡å¾—åˆ†
    fn calculate_quality_score(&self, data: &MarketData, rules: &ExchangeNormalizationRules) -> f64 {
        let mut score = 1.0;

        // æª¢æŸ¥å®Œæ•´æ€§
        for check in &rules.completeness_checks {
            match check {
                CompletenessCheck::RequireBidAsk => {
                    if data.bid.is_zero() || data.ask.is_zero() {
                        score -= 0.2;
                    }
                },
                CompletenessCheck::RequireVolume => {
                    if data.volume.is_zero() {
                        score -= 0.2;
                    }
                },
                CompletenessCheck::RequireOrderSize => {
                    if data.bid_size.is_zero() || data.ask_size.is_zero() {
                        score -= 0.1;
                    }
                },
                CompletenessCheck::RequirePriceChange => {
                    // éœ€è¦é¡å¤–çš„æ­·å²æ•¸æ“šä¾†è¨ˆç®—
                    score -= 0.05;
                },
            }
        }

        // æª¢æŸ¥åƒ¹æ ¼åˆç†æ€§
        if data.bid >= data.ask {
            score -= 0.3;
        }

        // ç¢ºä¿åˆ†æ•¸åœ¨åˆç†ç¯„åœå…§
        score.max(0.0).min(1.0)
    }

    /// é©—è­‰æ¨™æº–åŒ–å¾Œçš„æ•¸æ“š
    fn validate_standardized_data(&self, data: &StandardizedMarketData) -> Result<(), ExchangeError> {
        // åŸºæœ¬å­—æ®µæª¢æŸ¥
        if data.symbol.is_empty() {
            return Err(ExchangeError::DataError("Empty symbol".to_string()));
        }

        if data.price.is_zero() || data.price.is_sign_negative() {
            return Err(ExchangeError::DataError("Invalid price".to_string()));
        }

        // è²·è³£åƒ¹å·®æª¢æŸ¥
        if !data.bid.is_zero() && !data.ask.is_zero() {
            let spread = (data.ask - data.bid) / data.price;
            if spread.to_f64().unwrap_or(0.0) > self.validation_rules.max_spread_percentage {
                return Err(ExchangeError::DataError(
                    format!("Spread too large: {:.4}%", spread.to_f64().unwrap_or(0.0) * 100.0)
                ));
            }
        }

        // æœ€å°äº¤æ˜“é‡æª¢æŸ¥
        if data.volume < self.validation_rules.min_volume {
            return Err(ExchangeError::DataError("Volume below minimum".to_string()));
        }

        // æ™‚é–“å»¶é²æª¢æŸ¥
        let now = Utc::now();
        let delay_ms = (now - data.timestamp).num_milliseconds();
        if delay_ms > self.validation_rules.max_time_delay_ms {
            return Err(ExchangeError::DataError(
                format!("Data too old: {}ms delay", delay_ms)
            ));
        }

        // è³ªé‡åˆ†æ•¸æª¢æŸ¥
        if data.quality_score < 0.5 {
            return Err(ExchangeError::DataError(
                format!("Data quality too low: {:.2}", data.quality_score)
            ));
        }

