}

/// æœ¬åœ°æ•¸æ“šç·©å­˜å’Œå‚™ä»½ç³»çµ±
pub struct LocalDataCache {
    /// é…ç½®
    config: LocalCacheConfig,
    /// è¨˜æ†¶é«”ç·©å­˜ (symbol -> æ™‚é–“åºåˆ—æ•¸æ“š)
    memory_cache: Arc<RwLock<BTreeMap<String, BTreeMap<DateTime<Utc>, CacheItem>>>>,
    /// ç·©å­˜çµ±è¨ˆ
