}

/// ç·©å­˜çµ±è¨ˆä¿¡æ¯
#[derive(Debug, Clone)]
pub struct CacheStats {
    pub memory_usage: usize,
    pub disk_usage_bytes: u64,
    pub cache_hits: u64,
    pub cache_misses: u64,
    pub backup_count: u64,
