}

impl LocalDataCache {
    /// å‰µå»ºæ–°çš„æœ¬åœ°æ•¸æ“šç·©å­˜ç³»çµ±
    pub async fn new(config: LocalCacheConfig) -> Result<Self, ExchangeError> {
        // å‰µå»ºå¿…è¦çš„ç›®éŒ„
        fs::create_dir_all(&config.cache_directory)
            .map_err(|e| ExchangeError::ServiceError(format!("Failed to create cache directory: {}", e)))?;

        fs::create_dir_all(&config.backup_directory)
            .map_err(|e| ExchangeError::ServiceError(format!("Failed to create backup directory: {}", e)))?;

        let cache = Self {
            config,
            memory_cache: Arc::new(RwLock::new(BTreeMap::new())),
            cache_stats: Arc::new(RwLock::new(CacheStats::default())),
            is_running: Arc::new(RwLock::new(false)),
        };

        info!("Local data cache system initialized");
        Ok(cache)
    }

    /// å•Ÿå‹•ç·©å­˜ç³»çµ±
    pub async fn start(&self) -> Result<(), ExchangeError> {
        let mut is_running = self.is_running.write().await;
        if *is_running {
            return Err(ExchangeError::ServiceError("Cache system already running".to_string()));
        }
        *is_running = true;

        info!("Starting local data cache system");

        // è¼‰å…¥ç£ç›¤ç·©å­˜
        self.load_disk_cache().await?;

        // å•Ÿå‹•å‚™ä»½ä»»å‹™
        self.start_backup_task().await;

        // å•Ÿå‹•æ¸…ç†ä»»å‹™
        self.start_cleanup_task().await;

        Ok(())
    }

    /// åœæ­¢ç·©å­˜ç³»çµ±
    pub async fn stop(&self) -> Result<(), ExchangeError> {
        let mut is_running = self.is_running.write().await;
        if !*is_running {
            return Ok(());
        }
        *is_running = false;

        info!("Stopping local data cache system");

        // åŸ·è¡Œæœ€çµ‚å‚™ä»½
        self.create_backup().await?;

        // ä¿å­˜è¨˜æ†¶é«”ç·©å­˜åˆ°ç£ç›¤
        self.save_memory_cache_to_disk().await?;

        Ok(())
    }

    /// ç·©å­˜æ•¸æ“š
    pub async fn cache_data(&self, data: StandardizedMarketData) -> Result<(), ExchangeError> {
        let mut cache = self.memory_cache.write().await;
        let mut stats = self.cache_stats.write().await;

        let symbol = data.symbol.clone();
        let timestamp = data.timestamp;

        // å‰µå»ºç·©å­˜é …ç›®
        let cache_item = CacheItem {
            data,
            inserted_at: Utc::now(),
            access_count: 0,
            last_accessed: Utc::now(),
        };

        // æ’å…¥åˆ°è¨˜æ†¶é«”ç·©å­˜
        let symbol_cache = cache.entry(symbol.clone()).or_insert_with(BTreeMap::new);
        symbol_cache.insert(timestamp, cache_item);

        // æª¢æŸ¥è¨˜æ†¶é«”ç·©å­˜å¤§å°é™åˆ¶
        let total_items: usize = cache.values().map(|m| m.len()).sum();
        if total_items > self.config.memory_cache_limit {
            self.evict_old_data(&mut cache).await;
        }

        // æ›´æ–°çµ±è¨ˆ
        stats.memory_usage = total_items;

        debug!("Cached data for symbol: {} at {}", symbol, timestamp);
        Ok(())
    }

    /// æ‰¹é‡ç·©å­˜æ•¸æ“š
    pub async fn cache_data_batch(&self, data_batch: Vec<StandardizedMarketData>) -> Result<(), ExchangeError> {
        let mut cache = self.memory_cache.write().await;
        let mut stats = self.cache_stats.write().await;

        for data in data_batch {
            let symbol = data.symbol.clone();
            let timestamp = data.timestamp;

            let cache_item = CacheItem {
                data,
                inserted_at: Utc::now(),
                access_count: 0,
                last_accessed: Utc::now(),
            };

            let symbol_cache = cache.entry(symbol).or_insert_with(BTreeMap::new);
            symbol_cache.insert(timestamp, cache_item);
        }

        // æª¢æŸ¥è¨˜æ†¶é«”ç·©å­˜å¤§å°é™åˆ¶
        let total_items: usize = cache.values().map(|m| m.len()).sum();
        if total_items > self.config.memory_cache_limit {
            self.evict_old_data(&mut cache).await;
        }

        // æ›´æ–°çµ±è¨ˆ
        stats.memory_usage = total_items;

        Ok(())
    }

    /// ç²å–ç·©å­˜æ•¸æ“š
    pub async fn get_cached_data(
        &self,
        symbol: &str,
        start_time: Option<DateTime<Utc>>,
        end_time: Option<DateTime<Utc>>,
        limit: Option<usize>,
    ) -> Result<Vec<StandardizedMarketData>, ExchangeError> {
        let mut cache = self.memory_cache.write().await;
        let mut stats = self.cache_stats.write().await;

        if let Some(symbol_cache) = cache.get_mut(symbol) {
            // æ§‹å»ºæ™‚é–“ç¯„åœæŸ¥è©¢
            let mut query_results = Vec::new();

            let start = start_time.unwrap_or_else(|| Utc::now() - ChronoDuration::days(1));
            let end = end_time.unwrap_or_else(|| Utc::now());

            // æŸ¥è©¢æŒ‡å®šæ™‚é–“ç¯„åœå…§çš„æ•¸æ“š
            for (timestamp, cache_item) in symbol_cache.range(start..=end) {
                // æ›´æ–°è¨ªå•çµ±è¨ˆ
                let item = symbol_cache.get_mut(timestamp).unwrap();
                item.access_count += 1;
                item.last_accessed = Utc::now();

                query_results.push(cache_item.data.clone());

                // æª¢æŸ¥é™åˆ¶
                if let Some(limit_val) = limit {
                    if query_results.len() >= limit_val {
                        break;
                    }
                }
            }

            stats.cache_hits += 1;
            Ok(query_results)
        } else {
            // å˜—è©¦å¾ç£ç›¤ç·©å­˜è¼‰å…¥
            match self.load_symbol_from_disk(symbol).await {
                Ok(disk_data) => {
                    // å°‡ç£ç›¤æ•¸æ“šè¼‰å…¥åˆ°è¨˜æ†¶é«”ç·©å­˜
                    let mut symbol_cache = BTreeMap::new();
                    for data in disk_data.iter() {
                        let cache_item = CacheItem {
                            data: data.clone(),
                            inserted_at: Utc::now(),
                            access_count: 1,
                            last_accessed: Utc::now(),
                        };
                        symbol_cache.insert(data.timestamp, cache_item);
                    }
                    cache.insert(symbol.to_string(), symbol_cache);

                    stats.cache_misses += 1;

                    // éæ¿¾çµæœ
                    let start = start_time.unwrap_or_else(|| Utc::now() - ChronoDuration::days(1));
                    let end = end_time.unwrap_or_else(|| Utc::now());

                    let filtered_results: Vec<_> = disk_data.into_iter()
                        .filter(|data| data.timestamp >= start && data.timestamp <= end)
                        .take(limit.unwrap_or(usize::MAX))
                        .collect();

                    Ok(filtered_results)
                },
                Err(_) => {
                    stats.cache_misses += 1;
                    Ok(Vec::new())
                }
            }
        }
    }

    /// ç²å–ç·©å­˜çµ±è¨ˆ
    pub async fn get_cache_stats(&self) -> CacheStats {
        let stats = self.cache_stats.read().await;
        stats.clone()
    }

    /// æ‰‹å‹•å‰µå»ºå‚™ä»½
    pub async fn create_backup(&self) -> Result<(), ExchangeError> {
        let timestamp = Utc::now();
        let backup_filename = format!("cache_backup_{}.json", timestamp.format("%Y%m%d_%H%M%S"));
        let backup_path = self.config.backup_directory.join(backup_filename);

        let cache = self.memory_cache.read().await;

        // å°‡ç·©å­˜æ•¸æ“šåºåˆ—åŒ–
        let serialized_data = serde_json::to_string_pretty(&*cache)
            .map_err(|e| ExchangeError::ServiceError(format!("Failed to serialize cache: {}", e)))?;

        // å¯«å…¥å‚™ä»½æ–‡ä»¶
        if self.config.enable_compression {
            // å¯ä»¥åœ¨é€™è£¡å¯¦ç¾å£“ç¸®é‚è¼¯
            fs::write(&backup_path, &serialized_data)
                .map_err(|e| ExchangeError::ServiceError(format!("Failed to write backup: {}", e)))?;
        } else {
            fs::write(&backup_path, &serialized_data)
                .map_err(|e| ExchangeError::ServiceError(format!("Failed to write backup: {}", e)))?;
        }

        // æ›´æ–°çµ±è¨ˆ
        let mut stats = self.cache_stats.write().await;
        stats.backup_count += 1;
        stats.last_backup_time = Some(timestamp);

        info!("Created backup: {}", backup_path.display());

        // æ¸…ç†èˆŠå‚™ä»½
        self.cleanup_old_backups().await?;

        Ok(())
    }

    /// å¾å‚™ä»½æ¢å¾©
    pub async fn restore_from_backup(&self, backup_filename: &str) -> Result<(), ExchangeError> {
        let backup_path = self.config.backup_directory.join(backup_filename);

        if !backup_path.exists() {
            return Err(ExchangeError::ServiceError(format!("Backup file not found: {}", backup_filename)));
        }

        let backup_content = fs::read_to_string(&backup_path)
            .map_err(|e| ExchangeError::ServiceError(format!("Failed to read backup: {}", e)))?;

        let restored_cache: BTreeMap<String, BTreeMap<DateTime<Utc>, CacheItem>> = 
            serde_json::from_str(&backup_content)
                .map_err(|e| ExchangeError::ServiceError(format!("Failed to parse backup: {}", e)))?;

        // æ›¿æ›ç•¶å‰ç·©å­˜
        let mut cache = self.memory_cache.write().await;
        *cache = restored_cache;

        // æ›´æ–°çµ±è¨ˆ
        let mut stats = self.cache_stats.write().await;
        stats.memory_usage = cache.values().map(|m| m.len()).sum();

        info!("Restored cache from backup: {}", backup_filename);
        Ok(())
    }

    /// æ¸…ç†éæœŸæ•¸æ“š
    pub async fn cleanup_expired_data(&self) -> Result<(), ExchangeError> {
        let cutoff_time = Utc::now() - ChronoDuration::days(self.config.disk_cache_retention_days);

        // æ¸…ç†è¨˜æ†¶é«”ç·©å­˜
        let mut cache = self.memory_cache.write().await;
        let mut removed_count = 0;

        for symbol_cache in cache.values_mut() {
            let before_count = symbol_cache.len();
            symbol_cache.retain(|&timestamp, _| timestamp > cutoff_time);
            removed_count += before_count - symbol_cache.len();
        }

        // ç§»é™¤ç©ºçš„ç¬¦è™Ÿç·©å­˜
        cache.retain(|_, symbol_cache| !symbol_cache.is_empty());

        // æ¸…ç†ç£ç›¤ç·©å­˜
        self.cleanup_disk_cache(cutoff_time).await?;

        // æ›´æ–°çµ±è¨ˆ
        let mut stats = self.cache_stats.write().await;
        stats.cleanup_count += 1;
        stats.last_cleanup_time = Some(Utc::now());
        stats.memory_usage = cache.values().map(|m| m.len()).sum();

        info!("Cleaned up {} expired cache items", removed_count);
        Ok(())
    }

    /// é€å‡ºèˆŠæ•¸æ“š
    async fn evict_old_data(&self, cache: &mut BTreeMap<String, BTreeMap<DateTime<Utc>, CacheItem>>) {
        let target_size = self.config.memory_cache_limit * 80 / 100; // ä¿æŒ80%å®¹é‡

        // æ”¶é›†æ‰€æœ‰é …ç›®ä¸¦æŒ‰æœ€å¾Œè¨ªå•æ™‚é–“æ’åº
        let mut all_items = Vec::new();
        for (symbol, symbol_cache) in cache.iter() {
            for (timestamp, cache_item) in symbol_cache.iter() {
                all_items.push((symbol.clone(), *timestamp, cache_item.last_accessed));
            }
        }

        all_items.sort_by(|a, b| a.2.cmp(&b.2)); // æŒ‰æœ€å¾Œè¨ªå•æ™‚é–“æ’åº

        // ç§»é™¤æœ€èˆŠçš„é …ç›®
        let current_size: usize = cache.values().map(|m| m.len()).sum();
        let items_to_remove = current_size.saturating_sub(target_size);

        for (symbol, timestamp, _) in all_items.into_iter().take(items_to_remove) {
            if let Some(symbol_cache) = cache.get_mut(&symbol) {
                symbol_cache.remove(&timestamp);
            }
        }

        // æ¸…ç†ç©ºçš„ç¬¦è™Ÿç·©å­˜
        cache.retain(|_, symbol_cache| !symbol_cache.is_empty());

        debug!("Evicted {} old cache items", items_to_remove);
    }

    /// è¼‰å…¥ç£ç›¤ç·©å­˜
    async fn load_disk_cache(&self) -> Result<(), ExchangeError> {
        let cache_dir = &self.config.cache_directory;
        if !cache_dir.exists() {
            return Ok(());
        }

        // éæ­·ç·©å­˜ç›®éŒ„ä¸­çš„æ–‡ä»¶
        let entries = fs::read_dir(cache_dir)
            .map_err(|e| ExchangeError::ServiceError(format!("Failed to read cache directory: {}", e)))?;

        for entry in entries {
            let entry = entry.map_err(|e| ExchangeError::ServiceError(format!("Failed to read directory entry: {}", e)))?;
            let path = entry.path();

            if path.extension().and_then(|s| s.to_str()) == Some("json") {
                match self.load_cache_file(&path).await {
                    Ok(_) => debug!("Loaded cache file: {}", path.display()),
                    Err(e) => warn!("Failed to load cache file {}: {}", path.display(), e),
                }
            }
        }

        info!("Disk cache loaded");
        Ok(())
    }

    /// è¼‰å…¥å–®å€‹ç·©å­˜æ–‡ä»¶
    async fn load_cache_file(&self, path: &Path) -> Result<(), ExchangeError> {
        let content = fs::read_to_string(path)
            .map_err(|e| ExchangeError::ServiceError(format!("Failed to read cache file: {}", e)))?;

        let symbol_data: Vec<StandardizedMarketData> = serde_json::from_str(&content)
            .map_err(|e| ExchangeError::ServiceError(format!("Failed to parse cache file: {}", e)))?;

        // è¼‰å…¥åˆ°è¨˜æ†¶é«”ç·©å­˜
        self.cache_data_batch(symbol_data).await?;

        Ok(())
    }

    /// å¾ç£ç›¤è¼‰å…¥ç‰¹å®šç¬¦è™Ÿçš„æ•¸æ“š
    async fn load_symbol_from_disk(&self, symbol: &str) -> Result<Vec<StandardizedMarketData>, ExchangeError> {
        let cache_file = self.config.cache_directory.join(format!("{}.json", symbol));

        if !cache_file.exists() {
            return Ok(Vec::new());
        }

        let content = fs::read_to_string(&cache_file)
            .map_err(|e| ExchangeError::ServiceError(format!("Failed to read cache file: {}", e)))?;

        let data: Vec<StandardizedMarketData> = serde_json::from_str(&content)
            .map_err(|e| ExchangeError::ServiceError(format!("Failed to parse cache file: {}", e)))?;

        Ok(data)
    }

    /// ä¿å­˜è¨˜æ†¶é«”ç·©å­˜åˆ°ç£ç›¤
    async fn save_memory_cache_to_disk(&self) -> Result<(), ExchangeError> {
        let cache = self.memory_cache.read().await;

        for (symbol, symbol_cache) in cache.iter() {
            let data: Vec<StandardizedMarketData> = symbol_cache.values()
                .map(|item| item.data.clone())
                .collect();

            let cache_file = self.config.cache_directory.join(format!("{}.json", symbol));
            let serialized = serde_json::to_string_pretty(&data)
                .map_err(|e| ExchangeError::ServiceError(format!("Failed to serialize cache: {}", e)))?;

            fs::write(&cache_file, &serialized)
                .map_err(|e| ExchangeError::ServiceError(format!("Failed to write cache file: {}", e)))?;
        }

        info!("Memory cache saved to disk");
        Ok(())
    }

    /// æ¸…ç†ç£ç›¤ç·©å­˜
    async fn cleanup_disk_cache(&self, cutoff_time: DateTime<Utc>) -> Result<(), ExchangeError> {
        let cache_dir = &self.config.cache_directory;

        let entries = fs::read_dir(cache_dir)
            .map_err(|e| ExchangeError::ServiceError(format!("Failed to read cache directory: {}", e)))?;

        for entry in entries {
            let entry = entry.map_err(|e| ExchangeError::ServiceError(format!("Failed to read directory entry: {}", e)))?;
            let path = entry.path();

            if let Ok(metadata) = fs::metadata(&path) {
                if let Ok(modified_time) = metadata.modified() {
                    let modified_datetime = DateTime::<Utc>::from(modified_time);
                    if modified_datetime < cutoff_time {
                        if let Err(e) = fs::remove_file(&path) {
                            warn!("Failed to remove old cache file {}: {}", path.display(), e);
                        } else {
                            debug!("Removed old cache file: {}", path.display());
                        }
                    }
                }
            }
        }

        Ok(())
    }

    /// æ¸…ç†èˆŠå‚™ä»½
    async fn cleanup_old_backups(&self) -> Result<(), ExchangeError> {
        let backup_dir = &self.config.backup_directory;

        let mut backup_files = Vec::new();
        let entries = fs::read_dir(backup_dir)
            .map_err(|e| ExchangeError::ServiceError(format!("Failed to read backup directory: {}", e)))?;

        for entry in entries {
            let entry = entry.map_err(|e| ExchangeError::ServiceError(format!("Failed to read directory entry: {}", e)))?;
            let path = entry.path();

            if path.extension().and_then(|s| s.to_str()) == Some("json") {
                if let Ok(metadata) = fs::metadata(&path) {
                    if let Ok(modified_time) = metadata.modified() {
                        backup_files.push((path, modified_time));
                    }
                }
            }
        }

        // æŒ‰ä¿®æ”¹æ™‚é–“æ’åºï¼Œä¿ç•™æœ€æ–°çš„å‚™ä»½æ–‡ä»¶
        backup_files.sort_by(|a, b| b.1.cmp(&a.1));

        // åˆªé™¤è¶…éé™åˆ¶çš„å‚™ä»½æ–‡ä»¶
        for (path, _) in backup_files.into_iter().skip(self.config.max_backup_files) {
            if let Err(e) = fs::remove_file(&path) {
                warn!("Failed to remove old backup file {}: {}", path.display(), e);
            } else {
                debug!("Removed old backup file: {}", path.display());
            }
        }

        Ok(())
    }

    /// å•Ÿå‹•å‚™ä»½ä»»å‹™
    async fn start_backup_task(&self) {
        let cache_clone = Arc::new(self.clone());
        let is_running = Arc::clone(&self.is_running);
        let backup_interval = Duration::from_secs(self.config.backup_interval_minutes * 60);

        tokio::spawn(async move {
            let mut interval_timer = interval(backup_interval);

            while *is_running.read().await {
                interval_timer.tick().await;

                if let Err(e) = cache_clone.create_backup().await {
                    error!("Failed to create backup: {}", e);
                }
            }

            info!("Backup task stopped");
        });
    }

    /// å•Ÿå‹•æ¸…ç†ä»»å‹™
    async fn start_cleanup_task(&self) {
        let cache_clone = Arc::new(self.clone());
        let is_running = Arc::clone(&self.is_running);
        let cleanup_interval = Duration::from_secs(self.config.cleanup_interval_hours * 3600);

        tokio::spawn(async move {
            let mut interval_timer = interval(cleanup_interval);

            while *is_running.read().await {
                interval_timer.tick().await;

                if let Err(e) = cache_clone.cleanup_expired_data().await {
                    error!("Failed to cleanup expired data: {}", e);
                }
            }

            info!("Cleanup task stopped");
        });
    }
}

impl Clone for LocalDataCache {
    fn clone(&self) -> Self {
        Self {
            config: self.config.clone(),
            memory_cache: Arc::clone(&self.memory_cache),
            cache_stats: Arc::clone(&self.cache_stats),
            is_running: Arc::clone(&self.is_running),
        }
    }
}

impl Default for CacheStats {
    fn default() -> Self {
        Self {
            memory_usage: 0,
            disk_usage_bytes: 0,
            cache_hits: 0,
            cache_misses: 0,
            backup_count: 0,
            cleanup_count: 0,
            last_backup_time: None,
            last_cleanup_time: None,
        }
    }
}

