    /// æ‰‹å‹•å‰µå»ºå‚™ä»½
    pub async fn create_backup(&self) -> Result<(), ExchangeError> {
        let timestamp = Utc::now();
        let backup_filename = format!("cache_backup_{}.json", timestamp.format("%Y%m%d_%H%M%S"));
        let backup_path = self.config.backup_directory.join(backup_filename);

        let cache = self.memory_cache.read().await;

        // å°‡ç·©å­˜æ•¸æ“šåºåˆ—åŒ–
        let serialized_data = serde_json::to_string_pretty(&*cache)
            .map_err(|e| ExchangeError::ServiceError(format!("Failed to serialize cache: {}", e)))?;

        // å¯«å…¥å‚™ä»½æ–‡ä»¶
        if self.config.enable_compression {
            // å¯ä»¥åœ¨é€™è£¡å¯¦ç¾å£“ç¸®é‚è¼¯
            fs::write(&backup_path, &serialized_data)
                .map_err(|e| ExchangeError::ServiceError(format!("Failed to write backup: {}", e)))?;
        } else {
            fs::write(&backup_path, &serialized_data)
                .map_err(|e| ExchangeError::ServiceError(format!("Failed to write backup: {}", e)))?;
        }

        // æ›´æ–°çµ±è¨ˆ
        let mut stats = self.cache_stats.write().await;
        stats.backup_count += 1;
        stats.last_backup_time = Some(timestamp);

        info!("Created backup: {}", backup_path.display());

        // æ¸…ç†èˆŠå‚™ä»½
        self.cleanup_old_backups().await?;

        Ok(())
    }

    /// å¾å‚™ä»½æ¢å¾©
    pub async fn restore_from_backup(&self, backup_filename: &str) -> Result<(), ExchangeError> {
        let backup_path = self.config.backup_directory.join(backup_filename);

        if !backup_path.exists() {
            return Err(ExchangeError::ServiceError(format!("Backup file not found: {}", backup_filename)));
        }

        let backup_content = fs::read_to_string(&backup_path)
            .map_err(|e| ExchangeError::ServiceError(format!("Failed to read backup: {}", e)))?;

        let restored_cache: BTreeMap<String, BTreeMap<DateTime<Utc>, CacheItem>> = 
            serde_json::from_str(&backup_content)
                .map_err(|e| ExchangeError::ServiceError(format!("Failed to parse backup: {}", e)))?;

        // æ›¿æ›ç•¶å‰ç·©å­˜
        let mut cache = self.memory_cache.write().await;
        *cache = restored_cache;

        // æ›´æ–°çµ±è¨ˆ
        let mut stats = self.cache_stats.write().await;
        stats.memory_usage = cache.values().map(|m| m.len()).sum();

        info!("Restored cache from backup: {}", backup_filename);
        Ok(())
    }

    /// æ¸…ç†éæœŸæ•¸æ“š
    pub async fn cleanup_expired_data(&self) -> Result<(), ExchangeError> {
        let cutoff_time = Utc::now() - ChronoDuration::days(self.config.disk_cache_retention_days);

        // æ¸…ç†è¨˜æ†¶é«”ç·©å­˜
        let mut cache = self.memory_cache.write().await;
        let mut removed_count = 0;

        for symbol_cache in cache.values_mut() {
            let before_count = symbol_cache.len();
            symbol_cache.retain(|&timestamp, _| timestamp > cutoff_time);
            removed_count += before_count - symbol_cache.len();
        }

        // ç§»é™¤ç©ºçš„ç¬¦è™Ÿç·©å­˜
        cache.retain(|_, symbol_cache| !symbol_cache.is_empty());

        // æ¸…ç†ç£ç›¤ç·©å­˜
        self.cleanup_disk_cache(cutoff_time).await?;

        // æ›´æ–°çµ±è¨ˆ
        let mut stats = self.cache_stats.write().await;
        stats.cleanup_count += 1;
        stats.last_cleanup_time = Some(Utc::now());
        stats.memory_usage = cache.values().map(|m| m.len()).sum();

        info!("Cleaned up {} expired cache items", removed_count);
        Ok(())
    }

    /// é€å‡ºèˆŠæ•¸æ“š
    async fn evict_old_data(&self, cache: &mut BTreeMap<String, BTreeMap<DateTime<Utc>, CacheItem>>) {
        let target_size = self.config.memory_cache_limit * 80 / 100; // ä¿æŒ80%å®¹é‡

        // æ”¶é›†æ‰€æœ‰é …ç›®ä¸¦æŒ‰æœ€å¾Œè¨ªå•æ™‚é–“æ’åº
        let mut all_items = Vec::new();
        for (symbol, symbol_cache) in cache.iter() {
            for (timestamp, cache_item) in symbol_cache.iter() {
                all_items.push((symbol.clone(), *timestamp, cache_item.last_accessed));
            }
        }

        all_items.sort_by(|a, b| a.2.cmp(&b.2)); // æŒ‰æœ€å¾Œè¨ªå•æ™‚é–“æ’åº

        // ç§»é™¤æœ€èˆŠçš„é …ç›®
        let current_size: usize = cache.values().map(|m| m.len()).sum();
        let items_to_remove = current_size.saturating_sub(target_size);

        for (symbol, timestamp, _) in all_items.into_iter().take(items_to_remove) {
            if let Some(symbol_cache) = cache.get_mut(&symbol) {
                symbol_cache.remove(&timestamp);
            }
