/// å¸‚å ´æ•¸æ“šå­˜å„²æœå‹™
/// æä¾›çµ±ä¸€çš„æ•¸æ“šå­˜å„²å’ŒæŸ¥è©¢ä»‹é¢ï¼ŒåŒæ™‚ç®¡ç†ç·©å­˜å’ŒæŒä¹…åŒ–å­˜å„²
pub struct MarketDataStorageService {
    /// è¨˜æ†¶é«”ç·©å­˜
    cache: MarketDataCache,
    /// æ™‚åºæ•¸æ“šåº«
    time_series_db: Arc<TimeSeriesDatabase>,
    /// é…ç½®
    config: DataStorageConfig,
    /// å¾…å¯«å…¥çš„æ•¸æ“šç·©è¡å€
    write_buffer: Arc<RwLock<Vec<(MarketData, String)>>>,
    /// æœå‹™ç‹€æ…‹
    is_running: Arc<RwLock<bool>>,
}

impl MarketDataStorageService {
    /// å‰µå»ºæ–°çš„å¸‚å ´æ•¸æ“šå­˜å„²æœå‹™
    pub async fn new(config: DataStorageConfig) -> Result<Self, ExchangeError> {
        let time_series_db = Arc::new(TimeSeriesDatabase::new(config.time_series_config.clone()).await?);
        let cache = MarketDataCache::new(config.cache_size_limit);

        let service = Self {
            cache,
            time_series_db,
            config,
            write_buffer: Arc::new(RwLock::new(Vec::new())),
            is_running: Arc::new(RwLock::new(false)),
        };

        info!("Market data storage service initialized");

        Ok(service)
    }

    /// å•Ÿå‹•å­˜å„²æœå‹™
    pub async fn start(&self) -> Result<(), ExchangeError> {
        let mut is_running = self.is_running.write().await;
        if *is_running {
            return Err(ExchangeError::ServiceError("Service already running".to_string()));
        }
        *is_running = true;

        info!("Starting market data storage service");

        // å•Ÿå‹•æ‰¹é‡å¯«å…¥ä»»å‹™
        if !self.config.enable_real_time_write {
            self.start_batch_writer().await;
        }

        // å•Ÿå‹•å®šæœŸæ¸…ç†ä»»å‹™
        self.start_cleanup_task().await;

        Ok(())
    }

    /// åœæ­¢å­˜å„²æœå‹™
    pub async fn stop(&self) -> Result<(), ExchangeError> {
        let mut is_running = self.is_running.write().await;
        if !*is_running {
            return Ok(());
        }
        *is_running = false;

        info!("Stopping market data storage service");

        // å¯«å…¥å‰©é¤˜çš„ç·©è¡æ•¸æ“š
        self.flush_write_buffer().await?;

        Ok(())
    }

    /// å­˜å„²å¸‚å ´æ•¸æ“š
    pub async fn store_market_data(
        &self,
        data: MarketData,
        exchange: String,
    ) -> Result<(), ExchangeError> {
        // æ›´æ–°ç·©å­˜
        self.cache.update_market_data(data.clone()).await?;

        if self.config.enable_real_time_write {
            // å¯¦æ™‚å¯«å…¥æ•¸æ“šåº«
            self.time_series_db.write_market_data(&data, &exchange).await?;
        } else {
            // æ·»åŠ åˆ°å¯«å…¥ç·©è¡å€
            let mut buffer = self.write_buffer.write().await;
            buffer.push((data, exchange));

            // å¦‚æœç·©è¡å€éå¤§ï¼Œç«‹å³å¯«å…¥
            if buffer.len() >= self.config.time_series_config.batch_size {
                drop(buffer); // é‡‹æ”¾é–
                self.flush_write_buffer().await?;
            }
        }

        Ok(())
    }

    /// æ‰¹é‡å­˜å„²å¸‚å ´æ•¸æ“š
    pub async fn store_market_data_batch(
        &self,
        data_batch: Vec<(MarketData, String)>,
    ) -> Result<(), ExchangeError> {
        if data_batch.is_empty() {
            return Ok(());
        }

        // æ›´æ–°ç·©å­˜
        for (data, _) in &data_batch {
            self.cache.update_market_data(data.clone()).await?;
        }

        if self.config.enable_real_time_write {
            // å¯¦æ™‚å¯«å…¥æ•¸æ“šåº«
            self.time_series_db.write_market_data_batch(&data_batch).await?;
        } else {
            // æ·»åŠ åˆ°å¯«å…¥ç·©è¡å€
            let mut buffer = self.write_buffer.write().await;
            buffer.extend(data_batch);

            // å¦‚æœç·©è¡å€éå¤§ï¼Œç«‹å³å¯«å…¥
            if buffer.len() >= self.config.time_series_config.batch_size {
                drop(buffer); // é‡‹æ”¾é–
                self.flush_write_buffer().await?;
            }
        }

        Ok(())
    }

    /// ç²å–æœ€æ–°å¸‚å ´æ•¸æ“š (å¾ç·©å­˜)
    pub async fn get_latest_data(&self, symbol: &str) -> Option<MarketData> {
        self.cache.get_latest_data(symbol).await
    }

    /// ç²å–æ‰€æœ‰æœ€æ–°å¸‚å ´æ•¸æ“š (å¾ç·©å­˜)
    pub async fn get_all_latest_data(&self) -> HashMap<String, MarketData> {
        self.cache.get_all_latest_data().await
    }

    /// ç²å–æ­·å²æ•¸æ“š (å„ªå…ˆå¾ç·©å­˜ï¼Œå¦‚æœä¸è¶³å‰‡å¾æ•¸æ“šåº«æŸ¥è©¢)
    /// åŒ…å«æŸ¥è©¢è¶…æ™‚æ©Ÿåˆ¶ - å¦‚æœæ•¸æ“šåº«æŸ¥è©¢è¶…æ™‚ï¼Œè¿”å›ç·©å­˜æ•¸æ“š
    pub async fn get_history(
        &self,
        symbol: &str,
        exchange: Option<&str>,
        start_time: Option<DateTime<Utc>>,
        end_time: Option<DateTime<Utc>>,
        limit: Option<usize>,
    ) -> Result<Vec<MarketData>, ExchangeError> {
        // é¦–å…ˆå˜—è©¦å¾ç·©å­˜ç²å–
        let cache_history = self.cache.get_history(symbol, limit).await;

        // å¦‚æœç·©å­˜ä¸­æœ‰è¶³å¤ çš„æ•¸æ“šï¼Œç›´æ¥è¿”å›
        if let Some(limit_val) = limit {
            if cache_history.len() >= limit_val {
                return Ok(cache_history);
            }
        }

        // å¦‚æœæ²’æœ‰æŒ‡å®šæ™‚é–“ç¯„åœï¼Œè¿”å›ç·©å­˜æ•¸æ“š
        let (start_time, end_time) = match (start_time, end_time) {
            (Some(start), Some(end)) => (start, end),
            _ => return Ok(cache_history),
        };

        // å˜—è©¦å¾æ•¸æ“šåº«æŸ¥è©¢æ­·å²æ•¸æ“šï¼Œå¸¶è¶…æ™‚è™•ç†
        let query_timeout = Duration::from_secs(5); // 5ç§’è¶…æ™‚
        let db_query = self.time_series_db
            .query_market_data(symbol, exchange, start_time, end_time, limit);

        match tokio::time::timeout(query_timeout, db_query).await {
            Ok(Ok(db_history)) => {
                // æ•¸æ“šåº«æŸ¥è©¢æˆåŠŸ
                Ok(db_history)
            }
            Ok(Err(db_error)) => {
                // æ•¸æ“šåº«æŸ¥è©¢å‡ºéŒ¯ï¼Œè¨˜éŒ„éŒ¯èª¤ä¸¦è¿”å›ç·©å­˜æ•¸æ“š
                warn!("Database query failed for symbol {}: {}. Returning cached data.", symbol, db_error);
                Ok(cache_history)
            }
            Err(_) => {
                // æŸ¥è©¢è¶…æ™‚ï¼Œè¨˜éŒ„æ€§èƒ½å•é¡Œä¸¦è¿”å›ç·©å­˜æ•¸æ“š (ç¬¦åˆé©—æ”¶æ¨™æº– 5.3)
                warn!("Database query timeout for symbol {}. Returning cached data.", symbol);
                Ok(cache_history)
            }
        }
    }

    /// è¨‚é–±å¸‚å ´æ•¸æ“šæ›´æ–°
    pub fn subscribe(&self) -> broadcast::Receiver<MarketData> {
        self.cache.subscribe()
    }

    /// ç²å–å­˜å„²æœå‹™çµ±è¨ˆä¿¡æ¯
    pub async fn get_storage_stats(&self) -> Result<StorageStats, ExchangeError> {
        let cache_stats = self.cache.get_cache_stats().await;
        let db_stats = self.time_series_db.get_database_stats().await?;
        let buffer_size = self.write_buffer.read().await.len();

        Ok(StorageStats {
            cache_symbols: cache_stats.total_cached_symbols,
            cache_entries: cache_stats.total_history_entries,
            database_records: db_stats.get("total_records").copied().unwrap_or(0),
            write_buffer_size: buffer_size,
            is_running: *self.is_running.read().await,
        })
    }

    /// æ¸…ç†éæœŸæ•¸æ“š
    pub async fn cleanup_expired_data(&self) -> Result<(), ExchangeError> {
        info!("Starting expired data cleanup");

        // æ¸…ç†æ™‚åºæ•¸æ“šåº«ä¸­çš„éæœŸæ•¸æ“š
        self.time_series_db.cleanup_expired_data().await?;

        info!("Expired data cleanup completed");

        Ok(())
    }

    /// æ‰‹å‹•åˆ·æ–°å¯«å…¥ç·©è¡å€
    pub async fn flush_write_buffer(&self) -> Result<(), ExchangeError> {
        let mut buffer = self.write_buffer.write().await;
        if buffer.is_empty() {
            return Ok(());
        }

        let data_to_write = buffer.drain(..).collect::<Vec<_>>();
        drop(buffer); // é‡‹æ”¾é–

        if !data_to_write.is_empty() {
            self.time_series_db.write_market_data_batch(&data_to_write).await?;
            info!("Flushed {} records from write buffer", data_to_write.len());
        }

        Ok(())
    }

    /// å•Ÿå‹•æ‰¹é‡å¯«å…¥ä»»å‹™
    async fn start_batch_writer(&self) {
        let write_buffer = Arc::clone(&self.write_buffer);
        let time_series_db = Arc::clone(&self.time_series_db);
        let is_running = Arc::clone(&self.is_running);
        let interval_duration = Duration::from_secs(self.config.batch_write_interval);

        tokio::spawn(async move {
            let mut interval_timer = interval(interval_duration);

            loop {
                interval_timer.tick().await;

                // æª¢æŸ¥æœå‹™æ˜¯å¦é‚„åœ¨é‹è¡Œ
                if !*is_running.read().await {
                    break;
                }

                // å¯«å…¥ç·©è¡æ•¸æ“š
                let mut buffer = write_buffer.write().await;
                if buffer.is_empty() {
                    continue;
                }

                let data_to_write = buffer.drain(..).collect::<Vec<_>>();
                drop(buffer); // é‡‹æ”¾é–

                if let Err(e) = time_series_db.write_market_data_batch(&data_to_write).await {
                    error!("Failed to write batch data: {}", e);
                } else {
                    info!("Batch wrote {} records", data_to_write.len());
                }
            }

            info!("Batch writer task stopped");
        });
    }

    /// å•Ÿå‹•å®šæœŸæ¸…ç†ä»»å‹™
    async fn start_cleanup_task(&self) {
        let time_series_db = Arc::clone(&self.time_series_db);
        let is_running = Arc::clone(&self.is_running);
        let cleanup_interval = Duration::from_secs(self.config.cleanup_interval_hours * 3600);

        tokio::spawn(async move {
            let mut interval_timer = interval(cleanup_interval);

            loop {
                interval_timer.tick().await;

                // æª¢æŸ¥æœå‹™æ˜¯å¦é‚„åœ¨é‹è¡Œ
                if !*is_running.read().await {
                    break;
                }

                // åŸ·è¡Œæ¸…ç†
                if let Err(e) = time_series_db.cleanup_expired_data().await {
                    error!("Failed to cleanup expired data: {}", e);
                }
            }

            info!("Cleanup task stopped");
        });
    }
}

