async fn save_memory_cache_to_disk(&self) -> Result<(), ExchangeError> {
    let cache = self.memory_cache.read().await;

    for (symbol, symbol_cache) in cache.iter() {
        let data: Vec<StandardizedMarketData> = symbol_cache.values()
            .map(|item| item.data.clone())
            .collect();

        // æ¸…ç†ç¬¦è™Ÿä¸­çš„ç‰¹æ®Šå­—ç¬¦ä»¥ç”¨ä½œæ–‡ä»¶å
        let safe_symbol = symbol.replace("/", "_").replace("\\", "_");
        let cache_file = self.config.cache_directory.join(format!("{}.json", safe_symbol));
        let serialized = serde_json::to_string_pretty(&data)
            .map_err(|e| ExchangeError::ServiceError(format!("Failed to serialize cache: {}", e)))?;

        fs::write(&cache_file, &serialized)
            .map_err(|e| ExchangeError::ServiceError(format!("Failed to write cache file: {}", e)))?;
    }

    info!("Memory cache saved to disk");
    Ok(())
}

