            .collect();

        let cache_file = self.config.cache_directory.join(format!("{}.json", symbol));
        let serialized = serde_json::to_string_pretty(&data)
            .map_err(|e| ExchangeError::ServiceError(format!("Failed to serialize cache: {}", e)))?;

        fs::write(&cache_file, &serialized)
            .map_err(|e| ExchangeError::ServiceError(format!("Failed to write cache file: {}", e)))?;
    }

    info!("Memory cache saved to disk");
    Ok(())
}

