    async fn evict_old_data(&self, cache: &mut BTreeMap<String, BTreeMap<DateTime<Utc>, CacheItem>>) {
        let target_size = self.config.memory_cache_limit * 80 / 100; // ä¿æŒ80%å®¹é‡

        // æ”¶é›†æ‰€æœ‰é …ç›®ä¸¦æŒ‰æœ€å¾Œè¨ªå•æ™‚é–“æ’åº
        let mut all_items = Vec::new();
        for (symbol, symbol_cache) in cache.iter() {
            for (timestamp, cache_item) in symbol_cache.iter() {
                all_items.push((symbol.clone(), *timestamp, cache_item.last_accessed));
            }
        }

        all_items.sort_by(|a, b| a.2.cmp(&b.2)); // æŒ‰æœ€å¾Œè¨ªå•æ™‚é–“æ’åº

        // ç§»é™¤æœ€èˆŠçš„é …ç›®
        let current_size: usize = cache.values().map(|m| m.len()).sum();
        let items_to_remove = current_size.saturating_sub(target_size);

        for (symbol, timestamp, _) in all_items.into_iter().take(items_to_remove) {
            if let Some(symbol_cache) = cache.get_mut(&symbol) {
                symbol_cache.remove(&timestamp);
            }
        }

        // æ¸…ç†ç©ºçš„ç¬¦è™Ÿç·©å­˜
        cache.retain(|_, symbol_cache| !symbol_cache.is_empty());

