"""Performance Metrics Calculator"""

import pandas as pd
import numpy as np
from typing import Dict, List, Optional
from decimal import Decimal


class PerformanceMetrics:
    """Calculator for trading performance metrics."""

    @staticmethod
    def total_return(initial_value: float, final_value: float) -> float:
        """Calculate total return percentage."""
        return (final_value - initial_value) / initial_value

    @staticmethod
    def annualized_return(returns: pd.Series, trading_days: int = 252) -> float:
        """Calculate annualized return."""
        total_return = (1 + returns).prod() - 1
        years = len(returns) / trading_days
        return (1 + total_return) ** (1 / years) - 1

    @staticmethod
    def sharpe_ratio(returns: pd.Series, risk_free_rate: float = 0.02, trading_days: int = 252) -> float:
        """Calculate Sharpe ratio."""
        excess_returns = returns - (risk_free_rate / trading_days)
        return np.sqrt(trading_days) * excess_returns.mean() / excess_returns.std()

    @staticmethod
    def max_drawdown(equity_curve: pd.Series) -> float:
        """Calculate maximum drawdown."""
        running_max = equity_curve.cummax()
        drawdown = (equity_curve - running_max) / running_max
        return drawdown.min()

    @staticmethod
    def volatility(returns: pd.Series, trading_days: int = 252) -> float:
        """Calculate annualized volatility."""
        return returns.std() * np.sqrt(trading_days)

    @staticmethod
    def win_rate(returns: pd.Series) -> float:
        """Calculate win rate (percentage of positive returns)."""
        winning_trades = (returns > 0).sum()
        total_trades = len(returns)
        return winning_trades / total_trades if total_trades > 0 else 0.0

    @staticmethod
    def calmar_ratio(returns: pd.Series, trading_days: int = 252) -> float:
        """Calculate Calmar ratio (annualized return / max drawdown)."""
        ann_return = PerformanceMetrics.annualized_return(returns, trading_days)
        max_dd = abs(PerformanceMetrics.max_drawdown((1 + returns).cumprod()))
        return ann_return / max_dd if max_dd != 0 else 0.0

